import React, { useState } from "react";
import API from "../API/api";
import SignUpModal from "./signUpModal";
import logo from "../Assets/logo.png";
import { useUser } from "../context/usercontext";

export default function Loginbox() {
  const { setRole, setUser } = useUser();
  const [form, setForm] = useState({ name: "", password: "" });
  const [isOpen, setIsOpen] = useState(false);
  const [buttonActivate, setButtonActivate] = useState(true);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await API.post("/user/login", form);
      localStorage.setItem("token", res.data.token);

      const role = await API.get("/user/role", {
        headers: { Authorization: `Bearer ${res.data.token}` },
      });

      setUser(role.data.name);
      setRole(role.data.role);

      localStorage.setItem("role", role.data.role);
      localStorage.setItem("user", JSON.stringify({ name: role.data.name }));
      alert(`Logged in Successfully`);
    } catch (error) {
      alert(`Login Failed ${error}`);
    }
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-white px-4">
      {/* Logo & Title */}
      <div className="relative w-full max-w-4xl h-56 md:h-64 mb-16">
        <img
          src={logo}
          alt="Background Logo"
          className="absolute inset-0 w-full h-full object-contain opacity-10 pointer-events-none select-none z-0"
        />
        <h1 className="text-twohas text-5xl md:text-6xl font-extrabold drop-shadow-lg select-none relative z-10 text-center flex items-center justify-center h-full">
          Twoha's Cafe
        </h1>
      </div>

      {/* Login Card */}
      <div className="bg-twohas p-8 rounded-2xl shadow-2xl w-full max-w-md relative z-20">
        <form className="space-y-6" onSubmit={handleSubmit}>
          <div>
            <label
              htmlFor="name"
              className="block text-white font-semibold mb-2"
            >
              Name
            </label>
            <input
              name="name"
              type="text"
              id="name"
              placeholder="Enter your name"
              onChange={handleChange}
              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-gray-400 transition"
            />
          </div>

          <div>
            <label
              htmlFor="password"
              className="block text-white font-semibold mb-2"
            >
              Password
            </label>
            <input
              name="password"
              type="password"
              id="password"
              placeholder="Enter your password"
              onChange={handleChange}
              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-gray-400 transition"
            />
          </div>

          <button
            type="submit"
            className="w-full bg-white text-twohas font-bold py-3 rounded-lg border-2 border-white hover:bg-twohas hover:text-white hover:border-white transition-all duration-300 shadow-md"
            disabled={!form.name || !form.password}
          >
            Login
          </button>

          {buttonActivate && (
            <button
              onClick={() => setIsOpen(true)}
              type="button"
              className="w-full bg-white text-twohas font-bold py-3 rounded-lg border-2 border-white hover:bg-twohas hover:text-white hover:border-white transition-all duration-300 shadow-md"
            >
              Sign Up
            </button>
          )}
        </form>
      </div>

      {/* Sign Up Modal */}
      <SignUpModal
        isOpen={isOpen}
        onClose={() => setIsOpen(false)}
        onSuccess={() => setButtonActivate(false)}
      />
    </div>
  );
}
