import React, { useState, useEffect } from "react";
import API from "../../API/api";

export default function CPA() {
  const [employees, setEmployees] = useState([]);
  const [selectedMonth, setSelectedMonth] = useState("current"); // current / previous
  const [selectedRange, setSelectedRange] = useState("1-15"); // 1-15 / 16-end
  const [scheduleData, setScheduleData] = useState({});
  const [dateRange, setDateRange] = useState([]);

  useEffect(() => {
    const fetchEmployees = async () => {
      try {
        const token = localStorage.getItem("token");
        const res = await API.get("/employee/employeeList", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setEmployees(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchEmployees();
  }, []);

  useEffect(() => {
    const today = new Date();
    let year = today.getFullYear();
    let month =
      selectedMonth === "current" ? today.getMonth() : today.getMonth() - 1;
    const startDay = selectedRange === "1-15" ? 1 : 16;
    const endDay =
      selectedRange === "1-15" ? 15 : new Date(year, month + 1, 0).getDate(); // 마지막일

    const dates = [];
    for (let d = startDay; d <= endDay; d++) {
      dates.push(new Date(year, month, d));
    }
    setDateRange(dates);
  }, [selectedMonth, selectedRange]);

  return (
    <div className="p-6 max-w-full mx-auto">
      <h2 className="text-3xl font-bold mb-6 text-center">CPA Schedule</h2>

      <div className="flex gap-4 mb-6">
        <div>
          <button
            className={`px-3 py-1 rounded ${
              selectedMonth === "current"
                ? "bg-twohas text-white"
                : "bg-gray-200"
            }`}
            onClick={() => setSelectedMonth("current")}
          >
            This Month
          </button>
          <button
            className={`px-3 py-1 rounded ml-2 ${
              selectedMonth === "previous"
                ? "bg-twohas text-white"
                : "bg-gray-200"
            }`}
            onClick={() => setSelectedMonth("previous")}
          >
            Previous Month
          </button>
        </div>

        <div>
          <button
            className={`px-3 py-1 rounded ${
              selectedRange === "1-15" ? "bg-twohas text-white" : "bg-gray-200"
            }`}
            onClick={() => setSelectedRange("1-15")}
          >
            1-15
          </button>
          <button
            className={`px-3 py-1 rounded ml-2 ${
              selectedRange === "16-end"
                ? "bg-twohas text-white"
                : "bg-gray-200"
            }`}
            onClick={() => setSelectedRange("16-end")}
          >
            16-End
          </button>
        </div>
      </div>

      {employees.map((emp) => (
        <div
          key={emp._id}
          className="mb-8 overflow-x-auto border rounded shadow"
        >
          <table className="min-w-max border-collapse table-auto w-full">
            <thead>
              <tr>
                <th className="border px-2 py-1 text-left">Name</th>
                <th className="border px-2 py-1 text-left">Hourly</th>
                {dateRange.map((date, idx) => (
                  <th key={idx} className="border px-2 py-1 text-center">
                    {`${date.getMonth() + 1}/${date.getDate()}`}
                  </th>
                ))}
              </tr>
              <tr>
                <th></th>
                <th></th>
                {dateRange.map((_, idx) => (
                  <th key={idx} className="border px-2 py-1 text-center">
                    <div className="grid grid-cols-2">
                      <span>AM</span>
                      <span>PM</span>
                    </div>
                  </th>
                ))}
              </tr>
              <tr>
                <th></th>
                <th></th>
                {dateRange.map((_, idx) => (
                  <th key={idx} className="border px-2 py-1 text-center">
                    <div className="grid grid-cols-4">
                      <span>Hourly</span>
                      <span>Tip</span>
                      <span>Hourly</span>
                      <span>Tip</span>
                    </div>
                  </th>
                ))}
              </tr>
            </thead>
            <tbody>
              <tr>
                <td className="border px-2 py-1">{emp.name}</td>
                <td className="border px-2 py-1">{emp.hourlyPay}</td>
                {dateRange.map((date, idx) => {
                  const key = `${date.getMonth() + 1}/${date.getDate()}`;
                  const am = scheduleData[emp._id]?.[key]?.AM || {};
                  const pm = scheduleData[emp._id]?.[key]?.PM || {};
                  return (
                    <td key={idx} className="border px-2 py-1">
                      <div className="grid grid-cols-4 gap-1 text-center">
                        <span>{am.hourly || 0}</span>
                        <span>{am.tip || 0}</span>
                        <span>{pm.hourly || 0}</span>
                        <span>{pm.tip || 0}</span>
                      </div>
                    </td>
                  );
                })}
              </tr>
            </tbody>
          </table>
        </div>
      ))}
    </div>
  );
}
