import React, { useState } from "react";

export default function Tv({ ticketNumber, guestName, onSubmit }) {
  const [ticket, setTicket] = useState(ticketNumber || "");
  const [guest, setGuest] = useState(guestName || "");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (onSubmit) onSubmit({ ticket, guest });
  };

  return (
    <div className="max-w-xs w-full bg-white rounded-2xl shadow-lg overflow-hidden hover:shadow-2xl transition-shadow p-6 m-4">
      <div className="flex flex-col gap-3 mb-4">
        <div className="text-sm text-gray-500">Ticket Number</div>
        <div className="text-xl font-bold text-twohas">{ticket}</div>
        <div className="text-sm text-gray-500">Guest Name</div>
        <div className="text-lg font-medium text-gray-800">{guest}</div>
      </div>

      <form onSubmit={handleSubmit} className="flex flex-col gap-2">
        <input
          type="text"
          placeholder="Ticket Number"
          value={ticket}
          onChange={(e) => setTicket(e.target.value)}
          className="border px-3 py-2 rounded-lg text-sm focus:outline-none focus:ring-2 focus:ring-twohas"
        />
        <input
          type="text"
          placeholder="Guest Name"
          value={guest}
          onChange={(e) => setGuest(e.target.value)}
          className="border px-3 py-2 rounded-lg text-sm focus:outline-none focus:ring-2 focus:ring-twohas"
        />
        <button
          type="submit"
          className="mt-2 bg-twohas text-white px-4 py-2 rounded-lg font-semibold hover:bg-twohas/80 transition"
        >
          Submit
        </button>
      </form>
    </div>
  );
}
