import React, { useState, useEffect } from "react";
import API from "../../API/api";

export default function CPAReport() {
  const [employees, setEmployees] = useState([]);
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const [dates, setDates] = useState([]);

  useEffect(() => {
    const fetchEmployees = async () => {
      const token = localStorage.getItem("token");
      const res = await API.get("/employee/employeeList", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setEmployees(res.data);
    };
    fetchEmployees();
  }, []);

  useEffect(() => {
    if (!startDate || !endDate) return;
    const start = new Date(startDate);
    const end = new Date(endDate);
    const arr = [];
    let current = new Date(start);
    while (current <= end) {
      arr.push(new Date(current));
      current.setDate(current.getDate() + 1);
    }
    setDates(arr);
  }, [startDate, endDate]);

  return (
    <div className="p-6 max-w-full overflow-auto space-y-8">
      <h2 className="text-3xl font-bold mb-6 text-center text-gray-800">
        CPA Report
      </h2>
      <div className="flex gap-4 mb-6 items-center">
        <label>
          Start Date:{" "}
          <input
            type="date"
            value={startDate}
            onChange={(e) => setStartDate(e.target.value)}
            className="border px-2 py-1 rounded"
          />
        </label>
        <label>
          End Date:{" "}
          <input
            type="date"
            value={endDate}
            onChange={(e) => setEndDate(e.target.value)}
            className="border px-2 py-1 rounded"
          />
        </label>
      </div>

      {employees.map((emp) => (
        <div key={emp._id}>
          <table className="min-w-max w-full border-collapse mb-6">
            <thead className="sticky top-0 bg-gray-100 z-10">
              <tr>
                <th
                  rowSpan={3}
                  className="border px-3 py-2 bg-gray-200 text-left text-sm font-semibold"
                >
                  {emp.name}
                </th>
                <th
                  rowSpan={3}
                  className="border px-3 py-2 bg-gray-200 text-left text-sm font-semibold"
                >
                  ${emp.hourlyPay}
                </th>
                {dates.map((date) => (
                  <th
                    key={date}
                    colSpan={4}
                    className="border px-3 py-2 bg-gray-50 text-center text-sm font-semibold"
                  >
                    {date.getMonth() + 1}/{date.getDate()}
                  </th>
                ))}
              </tr>
              <tr>
                {dates.map((date) => (
                  <React.Fragment key={date}>
                    <th
                      colSpan={2}
                      className="border px-2 py-1 bg-gray-50 text-center text-xs font-medium"
                    >
                      AM
                    </th>
                    <th
                      colSpan={2}
                      className="border px-2 py-1 bg-gray-50 text-center text-xs font-medium"
                    >
                      PM
                    </th>
                  </React.Fragment>
                ))}
              </tr>
              <tr>
                {dates.map((date) => (
                  <React.Fragment key={date}>
                    <th className="border px-2 py-1 bg-gray-50 text-center text-xs font-medium">
                      Hourly
                    </th>
                    <th className="border px-2 py-1 bg-gray-50 text-center text-xs font-medium">
                      Tip
                    </th>
                    <th className="border px-2 py-1 bg-gray-50 text-center text-xs font-medium">
                      Hourly
                    </th>
                    <th className="border px-2 py-1 bg-gray-50 text-center text-xs font-medium">
                      Tip
                    </th>
                  </React.Fragment>
                ))}
              </tr>
            </thead>
            <tbody>
              <tr>
                {dates.map((date) => (
                  <React.Fragment key={date}>
                    <td className="border px-2 py-1">{/* AM Hourly */}</td>
                    <td className="border px-2 py-1">{/* AM Tip */}</td>
                    <td className="border px-2 py-1">{/* PM Hourly */}</td>
                    <td className="border px-2 py-1">{/* PM Tip */}</td>
                  </React.Fragment>
                ))}
              </tr>
            </tbody>
          </table>
        </div>
      ))}
    </div>
  );
}
