// CPAEmployeeGrid.jsx
import React from "react";

export default function CPAEmployeeGrid({ employees, scheduleData, dates }) {
  return (
    <div className="overflow-auto border rounded">
      <div className="grid grid-cols-[150px_100px_120px_repeat(auto-fit,minmax(80px,1fr))] auto-rows-min border-b">
        <div className="border p-2 row-span-4 font-bold text-center">Name</div>
        <div className="border p-2 row-span-4 font-bold text-center">
          Hourly
        </div>
        <div className="border p-2 row-span-4 font-bold text-center">
          Tax Report
        </div>
        {dates.map((day) => (
          <React.Fragment key={day}>
            <div className="border p-1 text-center font-semibold">{day}</div>
            <div className="grid grid-cols-2 border">
              <div className="border text-center">AM</div>
              <div className="border text-center">PM</div>
            </div>
            <div className="grid grid-cols-4 border">
              <div className="border text-center">Hourly</div>
              <div className="border text-center">Tip</div>
              <div className="border text-center">Hourly</div>
              <div className="border text-center">Tip</div>
            </div>
          </React.Fragment>
        ))}
      </div>

      {employees.map((emp) => (
        <div
          key={emp._id}
          className="grid grid-cols-[150px_100px_120px_repeat(auto-fit,minmax(80px,1fr))] auto-rows-min border-t"
        >
          <div className="border p-2 row-span-4 text-center">{emp.name}</div>
          <div className="border p-2 row-span-4 text-center">{emp.hourly}</div>
          <div className="border p-2 row-span-4 text-center">
            {emp.taxReport || "Pending"}
          </div>
          {dates.map((day) => {
            const dayData = scheduleData[day] || {
              AM: { hourly: 0, tip: 0 },
              PM: { hourly: 0, tip: 0 },
            };
            return (
              <React.Fragment key={day}>
                <div className="border p-1 text-center"></div>
                <div className="border p-1 text-center"></div>
                <div className="border p-1 text-center">
                  {dayData.AM.hourly}
                </div>
                <div className="border p-1 text-center">{dayData.AM.tip}</div>
                <div className="border p-1 text-center">
                  {dayData.PM.hourly}
                </div>
                <div className="border p-1 text-center">{dayData.PM.tip}</div>
              </React.Fragment>
            );
          })}
        </div>
      ))}
    </div>
  );
}
