import React, { useState } from "react";
import API from "../API/api";
import SignUpModal from "./signUpModal";

export default function Loginbox() {
  const [form, setForm] = useState({ name: "", password: "" });
  const [message, setMessage] = useState("");
  const [isOpen, setIsOpen] = useState(false);
  const [buttonActivate, setButtonActivate] = useState(true);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
    console.log(form);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await API.post("/user/login", form);
      localStorage.setItem("token", res.data.token);
      setMessage("로그인성공");
      console.log(message);
    } catch (error) {
      setMessage(`로그인 실패 ${error}`);
      console.log(message);
    }
  };
  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-600 via-pink-500 to-red-500 flex flex-col items-center justify-center px-4">
      <h1 className="text-5xl font-extrabold text-white mb-12 drop-shadow-lg text-center select-none">
        Twoha's Management System
      </h1>
      <div className="bg-white rounded-xl shadow-2xl max-w-md w-full p-8">
        <form onSubmit={handleSubmit} className="space-y-6">
          <div>
            <label
              htmlFor="name"
              className="block text-gray-800 font-semibold mb-2"
            >
              Name
            </label>
            <input
              id="name"
              name="name"
              type="text"
              placeholder="Enter your name"
              onChange={handleChange}
              className="w-full px-5 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-4 focus:ring-pink-400 transition"
              required
              autoComplete="username"
            />
          </div>
          <div>
            <label
              htmlFor="password"
              className="block text-gray-800 font-semibold mb-2"
            >
              Password
            </label>
            <input
              id="password"
              name="password"
              type="password"
              placeholder="Enter your password"
              onChange={handleChange}
              className="w-full px-5 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-4 focus:ring-pink-400 transition"
              required
              autoComplete="current-password"
            />
          </div>
          <button
            type="submit"
            className="w-full bg-pink-500 hover:bg-pink-600 text-white font-bold py-3 rounded-lg shadow-md transition duration-300"
          >
            Login
          </button>
          {buttonActivate && (
            <button
              type="button"
              onClick={() => setIsOpen(true)}
              className="w-full mt-3 border-2 border-pink-500 text-pink-500 hover:bg-pink-50 font-semibold py-3 rounded-lg transition duration-300"
            >
              Sign up
            </button>
          )}
        </form>
        {message && (
          <p className="mt-6 text-center text-red-600 font-semibold select-text">
            {message}
          </p>
        )}
      </div>
      <SignUpModal
        isOpen={isOpen}
        onClose={() => setIsOpen(false)}
        onSuccess={() => setButtonActivate(false)}
      />
    </div>
  );
}
