import React, { useState } from "react";

export default function Recipe() {
  const [recipeName, setRecipeName] = useState("");
  const [ingredients, setIngredients] = useState([
    { name: "", volume: "" },
    { name: "", volume: "" },
    { name: "", volume: "" },
  ]);
  const [recipes, setRecipes] = useState([]);

  const handleIngredientChange = (index, field, value) => {
    const updated = [...ingredients];
    updated[index][field] = value;
    setIngredients(updated);
  };

  const addIngredientField = () => {
    setIngredients([...ingredients, { name: "", volume: "" }]);
  };

  const handleAddRecipe = () => {
    if (!recipeName.trim()) return;
    setRecipes([...recipes, { recipeName, ingredients }]);
    setRecipeName("");
    setIngredients([
      { name: "", volume: "" },
      { name: "", volume: "" },
      { name: "", volume: "" },
    ]);
  };

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h2 className="text-3xl font-bold mb-6 text-center">Recipe Manager</h2>

      <div className="bg-white p-6 rounded-2xl shadow mb-8">
        <div className="mb-4">
          <label className="block text-sm font-medium text-gray-600 mb-1">
            Recipe Name
          </label>
          <input
            type="text"
            placeholder="Ex: Pasta"
            value={recipeName}
            onChange={(e) => setRecipeName(e.target.value)}
            className="border px-3 py-2 rounded w-full focus:ring-2 focus:ring-blue-400 focus:outline-none"
          />
        </div>

        <div>
          <h3 className="text-lg font-semibold mb-2">Ingredients</h3>
          {ingredients.map((ing, idx) => (
            <div key={idx} className="grid grid-cols-2 gap-4 mb-2">
              <input
                type="text"
                placeholder="Ingredient"
                value={ing.name}
                onChange={(e) =>
                  handleIngredientChange(idx, "name", e.target.value)
                }
                className="border px-3 py-2 rounded focus:ring-2 focus:ring-blue-400 focus:outline-none"
              />
              <input
                type="text"
                placeholder="Volume (e.g. 200g, 1 cup)"
                value={ing.volume}
                onChange={(e) =>
                  handleIngredientChange(idx, "volume", e.target.value)
                }
                className="border px-3 py-2 rounded focus:ring-2 focus:ring-blue-400 focus:outline-none"
              />
            </div>
          ))}
          <button
            onClick={addIngredientField}
            className="mt-2 px-3 py-1 bg-gray-200 rounded hover:bg-gray-300 text-sm"
          >
            + Add Ingredient
          </button>
        </div>

        <div className="mt-6 flex justify-end">
          <button
            onClick={handleAddRecipe}
            className="px-4 py-2 bg-blue-500 text-white rounded-lg shadow hover:bg-blue-600"
          >
            Add Recipe
          </button>
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {recipes.map((recipe, idx) => (
          <div
            key={idx}
            className="bg-white p-4 rounded-2xl shadow hover:shadow-md transition"
          >
            <h3 className="text-xl font-bold mb-2">{recipe.recipeName}</h3>
            <ul className="list-disc pl-5 space-y-1">
              {recipe.ingredients
                .filter((ing) => ing.name.trim() || ing.volume.trim())
                .map((ing, i) => (
                  <li key={i} className="text-gray-700">
                    {ing.name && ing.volume
                      ? `${ing.name} - ${ing.volume}`
                      : ing.name || ing.volume}
                  </li>
                ))}
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
}
