import React, { useState } from "react";

export default function Tv() {
  const [ticketNumber, setTicketNumber] = useState("");
  const [guestName, setGuestName] = useState("");
  const [cards, setCards] = useState([]);

  const handleAddCard = () => {
    if (!ticketNumber || !guestName) return;
    setCards([...cards, { ticket: ticketNumber, guest: guestName }]);
    setTicketNumber("");
    setGuestName("");
  };

  return (
    <div className="p-6 min-h-screen bg-gray-50">
      <div className="flex gap-4 mb-6">
        <input
          type="text"
          placeholder="Ticket Number"
          value={ticketNumber}
          onChange={(e) => setTicketNumber(e.target.value)}
          className="border px-3 py-2 rounded-lg focus:outline-none focus:ring-2 focus:ring-twohas flex-1"
        />
        <input
          type="text"
          placeholder="Guest Name"
          value={guestName}
          onChange={(e) => setGuestName(e.target.value)}
          className="border px-3 py-2 rounded-lg focus:outline-none focus:ring-2 focus:ring-twohas flex-1"
        />
        <button
          onClick={handleAddCard}
          className="bg-twohas text-white px-4 py-2 rounded-lg hover:bg-twohas/80 transition"
        >
          Add
        </button>
      </div>

      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
        {cards.map((card, idx) => (
          <div
            key={idx}
            className="bg-white rounded-2xl shadow-lg p-4 flex flex-col gap-2"
          >
            <div className="text-sm text-gray-500">Ticket Number</div>
            <div className="text-xl font-bold text-twohas">{card.ticket}</div>
            <div className="text-sm text-gray-500">Guest Name</div>
            <div className="text-lg font-medium text-gray-800">
              {card.guest}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
