import React, { useState } from "react";

export default function UserRefund() {
  const today = new Date().toISOString().split("T")[0];
  const [refunds, setRefunds] = useState([
    {
      id: 1,
      item: "Latte",
      reason: "Customer complaint",
      createdAt: new Date(),
    },
    {
      id: 2,
      item: "Sandwich",
      reason: "Wrong order",
      createdAt: new Date("2024-07-31T18:00:00"),
    },
  ]);

  const [newRefund, setNewRefund] = useState({
    ticket: "",
    item: "",
    reason: "",
  });

  const filterToday = (date) =>
    new Date(date).toISOString().split("T")[0] === today;

  const handleInputChange = (field, value) => {
    setNewRefund((prev) => ({ ...prev, [field]: value }));
  };

  const handleAddRefund = () => {
    if (!newRefund.ticket || !newRefund.item || !newRefund.reason) return;
    setRefunds((prev) => [
      ...prev,
      {
        id: Date.now(),
        item: newRefund.item,
        ticket: newRefund.ticket,
        reason: newRefund.reason,
        createdAt: new Date(),
      },
    ]);
    setNewRefund({ ticket: "", item: "", reason: "" });
  };

  return (
    <section>
      <h2 className="text-xl font-bold mb-4 text-twohas">Refunds</h2>

      <div className="mb-4 p-4 border rounded-lg bg-gray-50">
        <div className="grid grid-cols-3 gap-3 mb-3">
          <input
            type="text"
            placeholder="Ticket#"
            value={newRefund.ticket}
            onChange={(e) => handleInputChange("ticket", e.target.value)}
            className="border px-2 py-1 rounded"
          />
          <input
            type="text"
            placeholder="Item"
            value={newRefund.item}
            onChange={(e) => handleInputChange("item", e.target.value)}
            className="border px-2 py-1 rounded"
          />
          <input
            type="text"
            placeholder="Reason"
            value={newRefund.reason}
            onChange={(e) => handleInputChange("reason", e.target.value)}
            className="border px-2 py-1 rounded"
          />
        </div>
        <button
          onClick={handleAddRefund}
          className="px-4 py-2 bg-twohas text-white rounded shadow hover:bg-twohas/80"
        >
          Add Refund
        </button>
      </div>

      <table className="w-full border-collapse border border-gray-200 text-sm rounded-lg overflow-hidden shadow">
        <thead className="bg-gray-100">
          <tr>
            <th className="border px-4 py-2">Ticket#</th>
            <th className="border px-4 py-2">Item</th>
            <th className="border px-4 py-2">Reason</th>
            <th className="border px-4 py-2">Date</th>
          </tr>
        </thead>
        <tbody>
          {refunds
            .filter((r) => filterToday(r.createdAt))
            .map((r) => (
              <tr key={r.id} className="odd:bg-white even:bg-gray-50">
                <td className="border px-4 py-2">{r.ticket}</td>
                <td className="border px-4 py-2">{r.item}</td>
                <td className="border px-4 py-2">{r.reason}</td>
                <td className="border px-4 py-2">
                  {new Date(r.createdAt).toLocaleString()}
                </td>
              </tr>
            ))}
        </tbody>
      </table>
    </section>
  );
}
