// CPA.js
import React, { useState, useEffect } from "react";
import API from "../../API/api";
import CPAEmployeeGrid from ".CPAComponents/CPAEmployeeGrid";

export default function CPA() {
  const [employees, setEmployees] = useState([]);
  const [scheduleData, setScheduleData] = useState({});
  const today = new Date();
  const year = today.getFullYear();
  const month = today.getMonth();
  const endDate = new Date(year, month + 1, 0).getDate();

  const firstHalfDates = Array.from(
    { length: Math.min(15, endDate) },
    (_, i) => i + 1
  );
  const secondHalfDates = Array.from(
    { length: endDate - 15 },
    (_, i) => i + 16
  );

  useEffect(() => {
    const fetchData = async () => {
      const token = localStorage.getItem("token");
      try {
        const empRes = await API.get("/employee/employeeList", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setEmployees(empRes.data);

        const scheduleRes = await API.get("/schedule/tipList", {
          headers: { Authorization: `Bearer ${token}` },
        });

        const mappedSchedule = {};
        scheduleRes.data.forEach((item) => {
          const dateObj = new Date(item.date);
          const key = dateObj.getDate();
          mappedSchedule[key] = {
            AM: { hourly: item.AM?.hourly || 0, tip: item.AM?.tipTotal || 0 },
            PM: { hourly: item.PM?.hourly || 0, tip: item.PM?.tipTotal || 0 },
          };
        });
        setScheduleData(mappedSchedule);
      } catch (err) {
        console.error(err);
      }
    };
    fetchData();
  }, []);

  return (
    <div className="p-6 max-w-full mx-auto">
      <CPAEmployeeGrid
        employees={employees}
        scheduleData={scheduleData}
        dates={firstHalfDates}
      />
      <CPAEmployeeGrid
        employees={employees}
        scheduleData={scheduleData}
        dates={secondHalfDates}
      />
    </div>
  );
}
