import React, { useState } from "react";

export default function ClockInOut() {
  const today = new Date().toISOString().split("T")[0];
  const [clockLogs] = useState([
    { id: 1, employee: "John Doe", type: "Clock In", time: new Date() },
    {
      id: 2,
      employee: "Jane Smith",
      type: "Clock Out",
      time: new Date("2024-08-01T09:00:00"),
    },
  ]);

  const filterToday = (date) =>
    new Date(date).toISOString().split("T")[0] === today;

  return (
    <section>
      <h2 className="text-xl font-bold mb-4 text-twohas">Clock In / Out</h2>
      <div className="flex gap-6 mb-6">
        <button className="px-6 py-3 text-lg font-semibold bg-twohas text-white rounded-xl shadow-lg hover:bg-twohas/80 transition">
          Clock In
        </button>
        <button className="px-6 py-3 text-lg font-semibold bg-red-500 text-white rounded-xl shadow-lg hover:bg-red-600 transition">
          Clock Out
        </button>
      </div>
      <table className="w-full border-collapse border border-gray-200 text-sm rounded-lg overflow-hidden shadow">
        <thead className="bg-gray-100">
          <tr>
            <th className="border px-4 py-2">Employee</th>
            <th className="border px-4 py-2">Type</th>
            <th className="border px-4 py-2">Time</th>
          </tr>
        </thead>
        <tbody>
          {clockLogs
            .filter((log) => filterToday(log.time))
            .map((log) => (
              <tr key={log.id} className="odd:bg-white even:bg-gray-50">
                <td className="border px-4 py-2">{log.employee}</td>
                <td className="border px-4 py-2">{log.type}</td>
                <td className="border px-4 py-2">
                  {new Date(log.time).toLocaleString()}
                </td>
              </tr>
            ))}
        </tbody>
      </table>
    </section>
  );
}
