import React from "react";

export default function CPAEmployeeGrid({
  employees,
  scheduleData,
  dateRange,
}) {
  const colTemplate = [
    "minmax(120px, 1fr)",
    "minmax(80px, 1fr)",
    "minmax(100px, 1fr)",
    ...Array(dateRange.length * 4).fill("minmax(60px, 1fr)"),
  ].join(" ");

  return (
    <div className="overflow-auto border rounded">
      <div
        className="grid auto-rows-min border-b"
        style={{ gridTemplateColumns: colTemplate }}
      >
        <div className="border p-2 row-span-3 font-bold text-center justify-center">
          Name
        </div>
        <div className="border p-2 row-span-3 font-bold text-center">
          Hourly
        </div>
        <div className="border p-2 row-span-3 font-bold text-center">
          Tax Report
        </div>

        {dateRange.map((date, idx) => (
          <div
            key={idx}
            className="border p-1 text-center font-semibold col-span-4"
          >
            {`${date.getMonth() + 1}/${date.getDate()}`}
          </div>
        ))}

        {dateRange.map((_, idx) => (
          <React.Fragment key={idx}>
            <div className="border text-center col-span-2 flex items-center justify-center">
              AM
            </div>
            <div className="border text-center col-span-2 flex items-center justify-center">
              PM
            </div>
          </React.Fragment>
        ))}

        {dateRange.map((_, idx) => (
          <React.Fragment key={idx}>
            <div className="border text-center flex items-center justify-center">
              Hourly
            </div>
            <div className="border text-center flex items-center justify-center">
              Tip
            </div>
            <div className="border text-center flex items-center justify-center">
              Hourly
            </div>
            <div className="border text-center flex items-center justify-center">
              Tip
            </div>
          </React.Fragment>
        ))}
      </div>

      {employees.map((emp) => (
        <div
          key={emp._id}
          className="grid auto-rows-min border-t"
          style={{ gridTemplateColumns: colTemplate }}
        >
          <div className="border p-2 row-span-4 text-center">{emp.name}</div>
          <div className="border p-2 row-span-4 text-center">
            {emp.hourlyPay}
          </div>
          <div className="border p-2 row-span-4 text-center">
            {emp.taxReport || "Pending"}
          </div>

          {dateRange.map((date, idx) => {
            const key = `${date.getMonth() + 1}/${date.getDate()}`;
            const am = scheduleData[emp._id]?.[key]?.AM || {};
            const pm = scheduleData[emp._id]?.[key]?.PM || {};
            return (
              <React.Fragment key={idx}>
                <div className="border p-1 text-center">{am.hourly || 0}</div>
                <div className="border p-1 text-center">{am.tip || 0}</div>
                <div className="border p-1 text-center">{pm.hourly || 0}</div>
                <div className="border p-1 text-center">{pm.tip || 0}</div>
              </React.Fragment>
            );
          })}
        </div>
      ))}
    </div>
  );
}
