// RecipeForm.js
import React, { useState } from "react";

export default function RecipeForm({ addRecipe }) {
  const [name, setName] = useState("");
  const [ingredients, setIngredients] = useState([
    { name: "", volume: "" },
    { name: "", volume: "" },
    { name: "", volume: "" },
  ]);

  const handleIngredientChange = (index, field, value) => {
    const updated = [...ingredients];
    updated[index][field] = value;
    setIngredients(updated);
  };

  const addIngredientField = () => {
    setIngredients([...ingredients, { name: "", volume: "" }]);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!name.trim()) return;
    addRecipe({ name, ingredients });
    setName("");
    setIngredients([
      { name: "", volume: "" },
      { name: "", volume: "" },
      { name: "", volume: "" },
    ]);
  };

  return (
    <form
      onSubmit={handleSubmit}
      className="bg-white shadow-md rounded-2xl p-6 mb-8"
    >
      <div className="mb-4">
        <label className="block text-sm font-medium text-gray-700 mb-1">
          Item
        </label>
        <input
          value={name}
          onChange={(e) => setName(e.target.value)}
          className="border border-gray-300 rounded-lg px-3 py-2 w-full focus:ring-2 focus:ring-blue-400 focus:outline-none"
          placeholder="Enter Item"
        />
      </div>

      <div>
        <label className="block text-sm font-medium text-gray-700 mb-2">
          Ingredients
        </label>
        {ingredients.map((ing, i) => (
          <div key={i} className="flex gap-3 mb-2">
            <input
              value={ing.name}
              onChange={(e) =>
                handleIngredientChange(i, "name", e.target.value)
              }
              placeholder="Ingredient"
              className="border border-gray-300 rounded-lg px-3 py-2 flex-1 focus:ring-2 focus:ring-blue-400 focus:outline-none"
            />
            <input
              value={ing.volume}
              onChange={(e) =>
                handleIngredientChange(i, "volume", e.target.value)
              }
              placeholder="Volume"
              className="border border-gray-300 rounded-lg px-3 py-2 w-32 focus:ring-2 focus:ring-blue-400 focus:outline-none"
            />
          </div>
        ))}
        <button
          type="button"
          onClick={addIngredientField}
          className="py-2 text-blue-500 text-sm font-medium mt-2 hover:underline"
        >
          + Add Ingredient
        </button>
      </div>

      <div className="flex justify-end mb-4">
        <button
          type="submit"
          className="bg-twohas hover:bg-gray-600 text-white text-lg font-semibold px-3 py-1 rounded-md shadow-sm transition"
        >
          Add Recipe
        </button>
      </div>
    </form>
  );
}
