import React, { useEffect, useState } from "react";
import API from "../../../API/api";

const formatTime = (timestamp) => {
  const date = new Date(timestamp);
  const datePart = date.toLocaleDateString(undefined, {
    month: "2-digit",
    day: "2-digit",
  });
  const timePart = date.toLocaleTimeString(undefined, {
    hour: "2-digit",
    minute: "2-digit",
  });
  return `${datePart} ${timePart}`;
};

// 화폐 단위 배열 (예시)
const BILL_DENOMINATIONS = [100, 50, 20, 10, 5, 1];
const COIN_DENOMINATIONS = [0.5, 0.25, 0.1, 0.05, 0.01];

export default function UserCashCount({ workday }) {
  const [cashCounts, setCashCounts] = useState([]);

  const [billInputs, setBillInputs] = useState(
    BILL_DENOMINATIONS.reduce((acc, d) => ({ ...acc, [d]: 0 }), {})
  );
  const [coinInputs, setCoinInputs] = useState(
    COIN_DENOMINATIONS.reduce((acc, d) => ({ ...acc, [d]: 0 }), {})
  );

  const fetchCashCounts = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await API.get("/summary/cashCountList", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setCashCounts(res.data);
    } catch (err) {
      console.error("Failed to fetch cash counts", err);
    }
  };

  const filterWorkday = (date) =>
    new Date(date).toLocaleDateString() ===
    new Date(workday).toLocaleDateString();

  useEffect(() => {
    fetchCashCounts();
  }, []);

  const calculateTotal = () => {
    const billTotal = Object.entries(billInputs).reduce(
      (sum, [denom, qty]) => sum + denom * qty,
      0
    );
    const coinTotal = Object.entries(coinInputs).reduce(
      (sum, [denom, qty]) => sum + denom * qty,
      0
    );
    return billTotal + coinTotal;
  };

  const handleInputChange = (type, denomination, value) => {
    const numberValue = Number(value);
    if (type === "bill") {
      setBillInputs((prev) => ({ ...prev, [denomination]: numberValue }));
    } else {
      setCoinInputs((prev) => ({ ...prev, [denomination]: numberValue }));
    }
  };

  const handleAddCashCount = () => {
    const totalAmount = calculateTotal();
    const newCashCount = {
      id: Date.now(),
      totalAmount,
      bills: { ...billInputs },
      coins: { ...coinInputs },
      createdAt: new Date(),
    };
    setCashCounts((prev) => [...prev, newCashCount]);
    setBillInputs(
      BILL_DENOMINATIONS.reduce((acc, d) => ({ ...acc, [d]: 0 }), {})
    );
    setCoinInputs(
      COIN_DENOMINATIONS.reduce((acc, d) => ({ ...acc, [d]: 0 }), {})
    );
  };

  return (
    <section>
      <h2 className="text-xl font-bold mb-4 text-twohas">Cash Count</h2>

      <div className="mb-6 p-4 border rounded-lg bg-gray-50">
        <h3 className="font-semibold text-lg mb-3">Add New Cash Count</h3>

        <div className="grid grid-cols-6 gap-4 mb-3">
          {BILL_DENOMINATIONS.map((d) => (
            <div key={d} className="flex flex-col items-center">
              <span className="text-sm font-medium">{d}$</span>
              <input
                type="number"
                min={0}
                value={billInputs[d]}
                onChange={(e) => handleInputChange("bill", d, e.target.value)}
                className="w-full border px-2 py-1 rounded text-center"
              />
            </div>
          ))}
        </div>

        <div className="grid grid-cols-6 gap-4 mb-3">
          {COIN_DENOMINATIONS.map((d) => (
            <div key={d} className="flex flex-col items-center">
              <span className="text-sm font-medium">{d}$</span>
              <input
                type="number"
                min={0}
                step="0.01"
                value={coinInputs[d]}
                onChange={(e) => handleInputChange("coin", d, e.target.value)}
                className="w-full border px-2 py-1 rounded text-center"
              />
            </div>
          ))}
        </div>

        <div className="flex justify-end">
          <button
            onClick={handleAddCashCount}
            className="px-5 py-2 bg-twohas text-white rounded-lg shadow hover:bg-twohas/80 font-semibold"
          >
            Add (${calculateTotal().toFixed(2)})
          </button>
        </div>
      </div>

      <table className="w-full border-collapse border border-gray-200 text-sm rounded-lg overflow-hidden shadow">
        <thead className="bg-gray-100">
          <tr>
            <th className="border px-4 py-2">Total</th>
            <th className="border px-4 py-2">Bills</th>
            <th className="border px-4 py-2">Coins</th>
            <th className="border px-4 py-2">Time</th>
          </tr>
        </thead>
        <tbody>
          {cashCounts
            .filter((count) =>
              filterWorkday(count.timestamp || count.createdAt)
            )
            .map((count) => (
              <tr key={count.id} className="odd:bg-white even:bg-gray-50">
                <td className="border px-4 py-2">${count.totalAmount}</td>
                <td className="border px-4 py-2">
                  {Object.entries(count.bills || {})
                    .filter(([denom, qty]) => qty > 0)
                    .map(([denom, qty]) => `${denom}x${qty}`)
                    .join(", ")}
                </td>
                <td className="border px-4 py-2">
                  {Object.entries(count.coins || {})
                    .filter(([denom, qty]) => qty > 0)
                    .map(([denom, qty]) => `${denom}x${qty}`)
                    .join(", ")}
                </td>
                <td className="border px-4 py-2">
                  {formatTime(count.timestamp || count.createdAt)}
                </td>
              </tr>
            ))}
        </tbody>
      </table>
    </section>
  );
}
