import React, { useState } from "react";

export default function RecipeTable({ recipes, updateRecipe, deleteRecipe }) {
  const [editingId, setEditingId] = useState(null);
  const [editedRecipe, setEditedRecipe] = useState(null);

  const handleEdit = (recipe) => {
    setEditingId(recipe.id);
    setEditedRecipe(JSON.parse(JSON.stringify(recipe)));
  };

  const handleIngredientChange = (index, field, value) => {
    const updated = [...editedRecipe.ingredients];
    updated[index][field] = value;
    setEditedRecipe({ ...editedRecipe, ingredients: updated });
  };

  const handleSave = () => {
    updateRecipe(editedRecipe);
    setEditingId(null);
    setEditedRecipe(null);
  };

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
      {recipes.map((recipe) => (
        <div
          key={recipe.id}
          className="bg-white shadow-md rounded-2xl p-6 flex flex-col"
        >
          {editingId === recipe.id ? (
            <>
              <input
                value={editedRecipe.name}
                onChange={(e) =>
                  setEditedRecipe({ ...editedRecipe, name: e.target.value })
                }
                className="border border-gray-300 rounded-lg px-3 py-2 mb-3 w-full focus:ring-2 focus:ring-blue-400 focus:outline-none"
              />
              {editedRecipe.ingredients.map((ing, i) => (
                <div key={i} className="flex gap-3 mb-2">
                  <input
                    value={ing.name}
                    onChange={(e) =>
                      handleIngredientChange(i, "name", e.target.value)
                    }
                    className="border border-gray-300 rounded-lg px-3 py-2 flex-1 focus:ring-2 focus:ring-blue-400 focus:outline-none"
                  />
                  <input
                    value={ing.volume}
                    onChange={(e) =>
                      handleIngredientChange(i, "volume", e.target.value)
                    }
                    className="border border-gray-300 rounded-lg px-3 py-2 w-32 focus:ring-2 focus:ring-blue-400 focus:outline-none"
                  />
                </div>
              ))}
              <div className="flex gap-3 mt-4">
                <button
                  onClick={handleSave}
                  className="bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded-lg shadow transition-transform transform hover:scale-105"
                >
                  Save
                </button>
                <button
                  onClick={() => setEditingId(null)}
                  className="bg-gray-400 hover:bg-gray-500 text-white px-4 py-2 rounded-lg shadow transition-transform transform hover:scale-105"
                >
                  Cancel
                </button>
              </div>
            </>
          ) : (
            <>
              <h3 className="text-xl font-semibold mb-3">{recipe.name}</h3>
              <div className="grid grid-rows-5 grid-flow-col gap-2 mb-4">
                {recipe.ingredients
                  .filter((ing) => ing.name.trim() || ing.volume.trim())
                  .map((ing, i) => (
                    <span
                      key={i}
                      className="px-3 py-1 bg-gray-100 rounded-lg text-sm text-gray-700 shadow-sm"
                    >
                      {ing.name && ing.volume
                        ? `${ing.name} - ${ing.volume}`
                        : ing.name || ing.volume}
                    </span>
                  ))}
              </div>

              <div className="flex gap-3 mt-auto">
                <button
                  onClick={() => handleEdit(recipe)}
                  className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-lg shadow transition-transform transform hover:scale-105"
                >
                  Edit
                </button>
                <button
                  onClick={() => deleteRecipe(recipe.id)}
                  className="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded-lg shadow transition-transform transform hover:scale-105"
                >
                  Delete
                </button>
              </div>
            </>
          )}
        </div>
      ))}
    </div>
  );
}
