import React, { useState, useEffect } from "react";
import API from "../../API/api";
import CPAEmployeeGrid from "./CPAComponents/CPAEmployeeGrid";

export default function CPA() {
  const today = new Date();
  const [selectedMonth, setSelectedMonth] = useState(today.getMonth());
  const [selectedYear, setSelectedYear] = useState(today.getFullYear());
  const [period, setPeriod] = useState("firstHalf");
  const [employees, setEmployees] = useState([]);
  const [scheduleData, setScheduleData] = useState({});

  const getEndDate = (year, month) => new Date(year, month + 1, 0).getDate();

  const getDatesForPeriod = () => {
    const endDate = getEndDate(selectedYear, selectedMonth);
    if (period === "firstHalf")
      return Array.from({ length: Math.min(15, endDate) }, (_, i) => i + 1);
    return Array.from({ length: endDate - 15 }, (_, i) => i + 16);
  };

  const dates = getDatesForPeriod();

  useEffect(() => {
    const fetchData = async () => {
      const token = localStorage.getItem("token");
      try {
        const empRes = await API.get("/employee/employeeList", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setEmployees(empRes.data);

        const scheduleRes = await API.get("/schedule/tipList", {
          headers: { Authorization: `Bearer ${token}` },
        });
        const mappedSchedule = {};
        scheduleRes.data.forEach((item) => {
          const dateObj = new Date(item.date);
          const key = dateObj.getDate();
          mappedSchedule[key] = {
            AM: { hourly: item.AM?.hourly || 0, tip: item.AM?.tipTotal || 0 },
            PM: { hourly: item.PM?.hourly || 0, tip: item.PM?.tipTotal || 0 },
          };
        });
        setScheduleData(mappedSchedule);
      } catch (err) {
        console.error(err);
      }
    };
    fetchData();
  }, [selectedMonth, selectedYear]);

  return (
    <div className="p-6 max-w-full mx-auto">
      <div className="flex items-center gap-4 mb-4">
        <select
          value={selectedYear}
          onChange={(e) => setSelectedYear(Number(e.target.value))}
          className="border rounded px-2 py-1"
        >
          {Array.from({ length: 5 }, (_, i) => today.getFullYear() - 2 + i).map(
            (y) => (
              <option key={y} value={y}>
                {y}
              </option>
            )
          )}
        </select>
        <select
          value={selectedMonth}
          onChange={(e) => setSelectedMonth(Number(e.target.value))}
          className="border rounded px-2 py-1"
        >
          {Array.from({ length: 12 }, (_, i) => (
            <option key={i} value={i}>
              {i + 1}ì›”
            </option>
          ))}
        </select>
        <select
          value={period}
          onChange={(e) => setPeriod(e.target.value)}
          className="border rounded px-2 py-1"
        >
          <option value="firstHalf">1~15</option>
          <option value="secondHalf">16~LastDate</option>
        </select>
      </div>

      <CPAEmployeeGrid
        employees={employees}
        scheduleData={scheduleData}
        dates={dates}
      />
    </div>
  );
}
