import React from "react";
import API from "../../../API/api";

export default function RecipeTable({
  recipes,
  setEditingId,
  editedRecipe,
  setEditedRecipe,
  editingId,
  fetchRecipe,
  ingredientsList,
}) {
  const handleEdit = (recipe) => {
    setEditingId(recipe._id);
    setEditedRecipe(recipe);
  };

  const handleDelete = async (_id) => {
    try {
      const token = localStorage.getItem("token");
      await API.post(
        "recipe/deleteRecipe",
        { id: _id },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      await fetchRecipe();
    } catch (error) {
      console.error("Failed to Delete", error);
    }
  };

  const handleIngredientChange = (_id, field, value) => {
    const newIngredients = editedRecipe.ingredients.map((ing) => {
      if (ing._id === _id) {
        if (field === "inventoryItem") {
          return { ...ing, inventoryItem: value };
        } else {
          return { ...ing, [field]: value };
        }
      }
      return ing;
    });
    setEditedRecipe({ ...editedRecipe, ingredients: newIngredients });
  };

  const addIngredientField = () => {
    setEditedRecipe({
      ...editedRecipe,
      ingredients: [
        ...editedRecipe.ingredients,
        {
          _id: editedRecipe.ingredients.inventoryItem._id,
          inventoryItem: null,
          volume: "",
        },
      ],
    });
  };

  const handleSave = async () => {
    try {
      const token = localStorage.getItem("token");
      const payload = {
        id: editedRecipe._id,
        name: editedRecipe.name,
        ingredients: editedRecipe.ingredients.map((ing) => ({
          inventoryItem: ing.inventoryItem._id,
          volume: ing.volume,
        })),
      };
      await API.put(`/recipe/updateRecipe/`, payload, {
        headers: { Authorization: `Bearer ${token}` },
      });
      await fetchRecipe();
      setEditingId(null);
    } catch (err) {
      console.error("Failed to save recipe", err);
    }
  };

  const handleCancel = () => {
    setEditingId(null);
  };

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
      {recipes.map((recipe) => (
        <div
          key={recipe._id}
          className="bg-white p-4 rounded-xl shadow flex flex-col justify-between"
        >
          {editingId === recipe._id ? (
            <>
              <input
                type="text"
                value={editedRecipe.name}
                onChange={(e) =>
                  setEditedRecipe({ ...editedRecipe, name: e.target.value })
                }
                className="border px-2 py-1 rounded mb-2 w-full"
              />
              <div className="grid gap-2 mb-2">
                {editedRecipe.ingredients.map((ing) => (
                  <div key={ing._id} className="flex gap-2">
                    <select
                      value={ing.inventoryItem?._id || ""}
                      onChange={(e) => {
                        const selectedItem = ingredientsList.find(
                          (item) => item._id === e.target.value
                        );
                        handleIngredientChange(
                          ing._id,
                          "inventoryItem",
                          selectedItem
                        );
                      }}
                      className="border px-2 py-1 rounded flex-1"
                    >
                      <option value="">Select ingredient</option>
                      {ingredientsList.map((item) => (
                        <option key={item._id} value={item._id}>
                          {item.name}
                        </option>
                      ))}
                    </select>
                    <input
                      value={ing.volume || ""}
                      onChange={(e) =>
                        handleIngredientChange(
                          ing._id,
                          "volume",
                          e.target.value
                        )
                      }
                      className="border px-2 py-1 rounded w-20"
                      placeholder="Vol"
                    />
                  </div>
                ))}
              </div>
              <button
                type="button"
                onClick={addIngredientField}
                className="py-1 text-blue-500 text-sm font-medium mt-2 hover:underline"
              >
                + Add Ingredient
              </button>
              <div className="flex gap-2 mt-2">
                <button
                  onClick={handleSave}
                  disabled={
                    !editedRecipe.name ||
                    editedRecipe.ingredients.some(
                      (ing) => !ing.inventoryItem?._id || !ing.volume
                    )
                  }
                  className={`bg-twohas hover:bg-gray-600 text-white text-sm font-semibold px-3 py-1 rounded-md shadow-sm transition ${
                    !editedRecipe.name ||
                    editedRecipe.ingredients.some(
                      (ing) => !ing.inventoryItem?._id || !ing.volume
                    )
                      ? "opacity-50 cursor-not-allowed"
                      : ""
                  }`}
                >
                  Save
                </button>

                <button
                  onClick={handleCancel}
                  className="bg-twohas hover:bg-gray-600 text-white text-sm font-semibold px-3 py-1 rounded-md shadow-sm transition"
                >
                  Cancel
                </button>
              </div>
            </>
          ) : (
            <>
              <h3 className="text-lg font-bold mb-2">{recipe.name}</h3>
              <div className="grid grid-rows-5 grid-flow-col gap-2 mb-2 border">
                {recipe.ingredients.map((ing) => (
                  <span
                    key={ing._id}
                    className="bg-gray-200 rounded px-2 py-1 text-sm"
                  >
                    {ing.inventoryItem?.name || "No Name"} ({ing.volume || 0})
                  </span>
                ))}
              </div>

              <div className="flex gap-2 mt-auto">
                <button
                  onClick={() => handleEdit(recipe)}
                  className="bg-twohas hover:bg-gray-600 text-white text-sm font-semibold px-3 py-1 rounded-md shadow-sm transition"
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDelete(recipe._id)}
                  className="bg-twohas hover:bg-gray-600 text-white text-sm font-semibold px-3 py-1 rounded-md shadow-sm transition"
                >
                  Delete
                </button>
              </div>
            </>
          )}
        </div>
      ))}
    </div>
  );
}
