import React, { useState } from "react";

export default function Tv() {
  const [ticketInput, setTicketInput] = useState("");
  const [guestInput, setGuestInput] = useState("");
  const [cards, setCards] = useState([]);
  const [showAll, setShowAll] = useState(false);

  const handleAddCard = () => {
    if (!ticketInput || !guestInput) return;
    setCards([...cards, { ticket: ticketInput, guest: guestInput }]);
    setTicketInput("");
    setGuestInput("");
  };

  return (
    <div className="p-6 space-y-6">
      <div className="bg-white rounded-xl shadow-md p-4 flex items-center gap-4">
        <input
          type="text"
          placeholder="Ticket #"
          value={ticketInput}
          onChange={(e) => setTicketInput(e.target.value)}
          className="border px-3 py-1 rounded-md text-sm w-24"
        />
        <input
          type="text"
          placeholder="Guest Name"
          value={guestInput}
          onChange={(e) => setGuestInput(e.target.value)}
          className="border px-3 py-1 rounded-md text-sm flex-1"
        />
        <button
          onClick={handleAddCard}
          className="bg-twohas text-white px-4 py-1 rounded-md hover:bg-twohas/80 transition"
        >
          Add
        </button>
        <button
          onClick={() => setShowAll(true)}
          className="bg-gray-300 text-black px-4 py-1 rounded-md hover:bg-gray-400 transition"
        >
          Show All
        </button>
      </div>

      <div className="bg-gray-100 rounded-xl p-4 grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-4">
        {cards.map((card, idx) => (
          <div
            key={idx}
            className="bg-twohas text-white rounded-xl shadow-lg p-4 flex flex-col justify-between hover:scale-105 transition-transform"
          >
            <div className="text-sm font-semibold">Ticket #</div>
            <div className="text-lg font-bold mb-2">K{card.ticket}</div>
            <div className="text-sm font-semibold">Guest Name</div>
            <div className="text-md">{card.guest}</div>
          </div>
        ))}
      </div>

      {showAll && (
        <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50 p-4">
          <div className="bg-white w-full h-full rounded-xl shadow-xl overflow-auto p-6 flex flex-col gap-4">
            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-4">
              {cards.map((card, idx) => (
                <div
                  key={idx}
                  className="bg-twohas text-white rounded-xl shadow-lg p-6 flex flex-col justify-between hover:scale-105 transition-transform"
                >
                  <div className="text-sm font-semibold">Ticket #</div>
                  <div className="text-lg font-bold mb-2">K{card.ticket}</div>
                  <div className="text-sm font-semibold">Guest Name</div>
                  <div className="text-md">{card.guest}</div>
                </div>
              ))}
            </div>
            <button
              onClick={() => setShowAll(false)}
              className="mt-4 self-center bg-twohas text-white px-6 py-2 rounded-md hover:bg-twohas/80 transition"
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
