import React, { useEffect, useState } from "react";
import API from "../../../API/api";

const formatTime = (timestamp) => {
  const date = new Date(timestamp);
  const datePart = date.toLocaleDateString(undefined, {
    month: "2-digit",
    day: "2-digit",
  });
  const timePart = date.toLocaleTimeString(undefined, {
    hour: "2-digit",
    minute: "2-digit",
  });
  return `${datePart} ${timePart}`;
};

export default function UserCashCount() {
  const today = new Date().toISOString().split("T")[0];
  const [cashCounts, setCashCounts] = useState([]);

  const fetchCashCounts = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await API.get("/summary/cashCountList", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setCashCounts(res.data);
    } catch (err) {
      console.error("Failed to fetch cash counts", err);
    }
  };

  const filterToday = (date) =>
    new Date(date).toISOString().split("T")[0] === today;

  useEffect(() => {
    fetchCashCounts();
  }, []);

  return (
    <section>
      <h2 className="text-xl font-bold mb-4 text-twohas">Cash Count</h2>
      <table className="w-full border-collapse border border-gray-200 text-sm rounded-lg overflow-hidden shadow">
        <thead className="bg-gray-100">
          <tr>
            <th className="border px-4 py-2">Total</th>
            <th className="border px-4 py-2">Bills</th>
            <th className="border px-4 py-2">Coins</th>
            <th className="border px-4 py-2">Time</th>
          </tr>
        </thead>
        <tbody>
          {cashCounts
            .filter((c) => filterToday(c.timestamp || c.createdAt))
            .map((c) => (
              <tr key={c.id} className="odd:bg-white even:bg-gray-50">
                <td className="border px-4 py-2">${c.totalAmount}</td>
                <td className="border px-4 py-2">
                  {Object.entries(c.bills || {})
                    .filter(([_, v]) => v > 0)
                    .map(([k, v]) => `${k}x${v}`)
                    .join(", ")}
                </td>
                <td className="border px-4 py-2">
                  {Object.entries(c.coins || {})
                    .filter(([_, v]) => v > 0)
                    .map(([k, v]) => `${k}x${v}`)
                    .join(", ")}
                </td>
                <td className="border px-4 py-2">
                  {formatTime(c.timestamp || c.createdAt)}
                </td>
              </tr>
            ))}
        </tbody>
      </table>
    </section>
  );
}
