import React, { useState, useEffect } from "react";
import API from "../../API/api";

export default function CPA() {
  const today = new Date();
  const [employees, setEmployees] = useState([]);
  const [selectedYear, setSelectedYear] = useState(today.getFullYear());
  const [selectedMonth, setSelectedMonth] = useState(today.getMonth());
  const [selectedRange, setSelectedRange] = useState("1-15");
  const [scheduleData, setScheduleData] = useState({});
  const [dateRange, setDateRange] = useState([]);

  useEffect(() => {
    const fetchEmployees = async () => {
      try {
        const token = localStorage.getItem("token");
        const res = await API.get("/employee/employeeList", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setEmployees(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchEmployees();
  }, []);

  useEffect(() => {
    const startDay = selectedRange === "1-15" ? 1 : 16;
    const endDay =
      selectedRange === "1-15"
        ? 15
        : new Date(selectedYear, selectedMonth + 1, 0).getDate();

    const dates = [];
    for (let d = startDay; d <= endDay; d++) {
      dates.push(new Date(selectedYear, selectedMonth, d));
    }
    setDateRange(dates);
  }, [selectedYear, selectedMonth, selectedRange]);

  useEffect(() => {
    const fetchScheduleData = async () => {
      try {
        const token = localStorage.getItem("token");
        const res = await API.get("/schedule/tipList", {
          headers: { Authorization: `Bearer ${token}` },
        });
        const mappedSchedule = {};
        res.data.forEach((item) => {
          const dateObj = new Date(item.date);
          const key = `${dateObj.getFullYear()}-${dateObj.getMonth()}-${dateObj.getDate()}`;
          mappedSchedule[key] = {
            AM: { hourly: item.AM?.hourly || 0, tip: item.AM?.tipTotal || 0 },
            PM: { hourly: item.PM?.hourly || 0, tip: item.PM?.tipTotal || 0 },
          };
        });
        setScheduleData(mappedSchedule);
      } catch (err) {
        console.error(err);
      }
    };
    fetchScheduleData();
  }, []);

  return (
    <div className="p-6 max-w-full mx-auto">
      <h2 className="text-3xl font-bold mb-6 text-center">CPA Schedule</h2>

      <div className="flex gap-4 mb-6">
        <select
          value={selectedYear}
          onChange={(e) => setSelectedYear(Number(e.target.value))}
          className="border rounded px-3 py-1"
        >
          {Array.from({ length: 5 }, (_, i) => today.getFullYear() - 2 + i).map(
            (y) => (
              <option key={y} value={y}>
                {y}
              </option>
            )
          )}
        </select>

        <select
          value={selectedMonth}
          onChange={(e) => setSelectedMonth(Number(e.target.value))}
          className="border rounded px-3 py-1"
        >
          {Array.from({ length: 12 }, (_, i) => (
            <option key={i} value={i}>
              {i + 1}ì›”
            </option>
          ))}
        </select>

        <select
          value={selectedRange}
          onChange={(e) => setSelectedRange(e.target.value)}
          className="border rounded px-3 py-1"
        >
          <option value="1-15">1-15</option>
          <option value="16-end">16-End</option>
        </select>
      </div>

      {employees.map((emp) => (
        <div
          key={emp._id}
          className="mb-8 overflow-x-auto border rounded shadow"
        >
          <table className="min-w-max border-collapse table-auto w-full">
            <thead>
              <tr>
                <th className="border px-2 py-1 text-left row-span-3">Name</th>
                <th className="border px-2 py-1 text-left row-span-3">
                  Hourly
                </th>
                <th className="border px-2 py-1 text-left row-span-3">
                  Tax Report
                </th>
                {dateRange.map((date, idx) => (
                  <th
                    key={idx}
                    className="border px-2 py-1 text-center"
                    colSpan={4}
                  >
                    {`${date.getMonth() + 1}/${date.getDate()}`}
                  </th>
                ))}
              </tr>
              <tr>
                {dateRange.map((_, idx) => (
                  <React.Fragment key={idx}>
                    <th className="border text-center" colSpan={2}>
                      AM
                    </th>
                    <th className="border text-center" colSpan={2}>
                      PM
                    </th>
                  </React.Fragment>
                ))}
              </tr>
              <tr>
                {dateRange.map((_, idx) => (
                  <React.Fragment key={idx}>
                    <th className="border text-center">Hourly</th>
                    <th className="border text-center">Tip</th>
                    <th className="border text-center">Hourly</th>
                    <th className="border text-center">Tip</th>
                  </React.Fragment>
                ))}
              </tr>
            </thead>

            <tbody>
              <tr>
                <td className="border px-2 py-1">{emp.name}</td>
                <td className="border px-2 py-1">{emp.hourlyPay}</td>
                <td className="border px-2 py-1">
                  {emp.taxReport || "Pending"}
                </td>
                {dateRange.map((date, idx) => {
                  const key = `${date.getFullYear()}-${date.getMonth()}-${date.getDate()}`;
                  const am = scheduleData[emp._id]?.[key]?.AM || {};
                  const pm = scheduleData[emp._id]?.[key]?.PM || {};
                  return (
                    <React.Fragment key={idx}>
                      <td className="border px-1 text-center">
                        {am.hourly || 0}
                      </td>
                      <td className="border px-1 text-center">{am.tip || 0}</td>
                      <td className="border px-1 text-center">
                        {pm.hourly || 0}
                      </td>
                      <td className="border px-1 text-center">{pm.tip || 0}</td>
                    </React.Fragment>
                  );
                })}
              </tr>
            </tbody>
          </table>
        </div>
      ))}
    </div>
  );
}
