import React from "react";

export default function CPAEmployeeGrid({
  employees,
  scheduleData,
  dateRange,
}) {
  return (
    <div className="overflow-auto border rounded">
      <div className="grid auto-rows-min border-b">
        <div className="border p-2 row-span-3 font-bold text-center w-32">
          Name
        </div>
        <div className="border p-2 row-span-3 font-bold text-center w-20">
          Hourly
        </div>
        <div className="border p-2 row-span-3 font-bold text-center w-24">
          Tax Report
        </div>

        {dateRange.map((date, idx) => (
          <div
            key={idx}
            className="border p-1 text-center font-semibold col-span-4 min-w-[60px]"
          >
            {`${date.getMonth() + 1}/${date.getDate()}`}
          </div>
        ))}

        {dateRange.map((_, idx) => (
          <React.Fragment key={idx}>
            <div className="border text-center col-span-2 flex items-center justify-center min-w-[60px]">
              AM
            </div>
            <div className="border text-center col-span-2 flex items-center justify-center min-w-[60px]">
              PM
            </div>
          </React.Fragment>
        ))}

        {dateRange.map((_, idx) => (
          <React.Fragment key={idx}>
            <div className="border text-center flex items-center justify-center min-w-[60px]">
              Hourly
            </div>
            <div className="border text-center flex items-center justify-center min-w-[60px]">
              Tip
            </div>
            <div className="border text-center flex items-center justify-center min-w-[60px]">
              Hourly
            </div>
            <div className="border text-center flex items-center justify-center min-w-[60px]">
              Tip
            </div>
          </React.Fragment>
        ))}
      </div>

      {employees.map((emp) => (
        <div key={emp._id} className="grid auto-rows-min border-t">
          <div className="border p-2 row-span-4 text-center w-32">
            {emp.name}
          </div>
          <div className="border p-2 row-span-4 text-center w-20">
            {emp.hourlyPay}
          </div>
          <div className="border p-2 row-span-4 text-center w-24">
            {emp.taxReport || "W-2"}
          </div>

          {dateRange.map((date, idx) => {
            const key = `${date.getMonth() + 1}/${date.getDate()}`;
            const am = scheduleData[key]?.AM || {};
            const pm = scheduleData[key]?.PM || {};
            return (
              <React.Fragment key={idx}>
                <div className="border p-1 text-center min-w-[60px]">
                  {am.hourly || 0}
                </div>
                <div className="border p-1 text-center min-w-[60px]">
                  {am.tip || 0}
                </div>
                <div className="border p-1 text-center min-w-[60px]">
                  {pm.hourly || 0}
                </div>
                <div className="border p-1 text-center min-w-[60px]">
                  {pm.tip || 0}
                </div>
              </React.Fragment>
            );
          })}
        </div>
      ))}
    </div>
  );
}
