import React, { useState, useRef, useEffect } from "react";

const EMPLOYEES = [
  "홍길동",
  "김민수",
  "최유진",
  "박서준",
  "이서윤",
  "강민재",
  "조민호",
];

function MultiSelectDropdown({ options, selected, onChange, disabled }) {
  const [open, setOpen] = useState(false);
  const containerRef = useRef();

  useEffect(() => {
    function handleClickOutside(event) {
      if (
        containerRef.current &&
        !containerRef.current.contains(event.target)
      ) {
        setOpen(false);
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  const toggleOption = (option) => {
    if (disabled) return;
    if (selected.includes(option)) {
      onChange(selected.filter((o) => o !== option));
    } else {
      onChange([...selected, option]);
    }
  };

  return (
    <div className="relative" ref={containerRef}>
      <button
        type="button"
        onClick={() => !disabled && setOpen((o) => !o)}
        className={`w-full border rounded px-2 py-1 text-left text-xs whitespace-normal break-words min-h-[2.5rem] ${
          disabled
            ? "bg-twohas text-white cursor-not-allowed"
            : "bg-white text-black"
        }`}
      >
        {selected.length === 0 ? "Select Employees" : selected.join(", ")}
      </button>

      {open && !disabled && (
        <div className="absolute z-10 mt-1 w-full max-h-48 overflow-auto rounded border bg-white shadow-lg text-xs">
          {options.map((option) => (
            <label
              key={option}
              className="flex items-center gap-2 px-3 py-1 cursor-pointer hover:bg-gray-100 select-none"
            >
              <input
                type="checkbox"
                checked={selected.includes(option)}
                onChange={() => toggleOption(option)}
                className="cursor-pointer"
              />
              {option}
            </label>
          ))}
        </div>
      )}
    </div>
  );
}

export default function Schedule() {
  const today = new Date();
  const year = today.getFullYear();
  const month = today.getMonth();

  const firstDay = new Date(year, month, 1).getDay();
  const lastDate = new Date(year, month + 1, 0).getDate();

  const calendarDays = [];
  for (let i = 0; i < firstDay; i++) calendarDays.push(null);
  for (let i = 1; i <= lastDate; i++) calendarDays.push(i);

  const remaining = 7 - (calendarDays.length % 7);
  if (remaining < 7) {
    for (let i = 1; i <= remaining; i++) {
      calendarDays.push(`next-${i}`);
    }
  }

  const [schedule, setSchedule] = useState({});

  const handleChange = (dateStr, shift, selectedEmployees) => {
    setSchedule((prev) => {
      const dayData = prev[dateStr] || {
        AM: { employees: [], confirmed: false },
        PM: { employees: [], confirmed: false },
      };
      return {
        ...prev,
        [dateStr]: {
          ...dayData,
          [shift]: {
            ...dayData[shift],
            employees: selectedEmployees,
          },
        },
      };
    });
  };

  const handleConfirm = (dateStr, shift) => {
    setSchedule((prev) => {
      const dayData = prev[dateStr] || {};
      const shiftData = dayData[shift] || { employees: [] };

      if (shiftData.employees.length === 0) {
        return prev;
      }

      return {
        ...prev,
        [dateStr]: {
          ...dayData,
          [shift]: {
            ...shiftData,
            confirmed: true,
          },
        },
      };
    });
  };

  return (
    <div className="p-6 max-w-7xl mx-auto">
      <h2 className="text-3xl font-bold mb-6 text-center">
        {year}-{month + 1}
      </h2>

      <div className="grid grid-cols-7 gap-4 text-center text-sm font-semibold text-gray-600 mb-4">
        {["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"].map((d) => (
          <div key={d} className="py-3 bg-gray-100 rounded-md shadow-sm">
            {d}
          </div>
        ))}
      </div>

      <div className="grid grid-cols-7 gap-4 text-sm">
        {calendarDays.map((day, index) => {
          let dateStr = null;
          let isNextMonth = false;

          if (typeof day === "number") {
            dateStr = `${year}-${String(month + 1).padStart(2, "0")}-${String(
              day
            ).padStart(2, "0")}`;
          } else if (typeof day === "string" && day.startsWith("next-")) {
            const nextDay = Number(day.split("-")[1]);
            const nextDate = new Date(year, month + 1, nextDay);
            dateStr = `${nextDate.getFullYear()}-${String(
              nextDate.getMonth() + 1
            ).padStart(2, "0")}-${String(nextDay).padStart(2, "0")}`;
            isNextMonth = true;
          }

          if (day === null) {
            return (
              <div
                key={index}
                className="h-64 min-w-[120px] bg-transparent border-none"
              />
            );
          }

          const dayData = schedule[dateStr] || {};

          return (
            <div
              key={index}
              className={`h-64 min-w-[120px] border rounded-xl bg-white shadow-md flex flex-col justify-between ${
                isNextMonth
                  ? "bg-gray-50 text-gray-400"
                  : "hover:shadow-lg transition-shadow"
              }`}
            >
              <div
                className={`px-3 pt-1 text-right text-s font-bold ${
                  isNextMonth ? "text-gray-300" : "text-gray-500"
                } h-6 select-none`}
              >
                {typeof day === "number" ? day : day.split("-")[1]}
              </div>

              {!isNextMonth &&
                ["AM", "PM"].map((shift) => {
                  const shiftData = dayData[shift] || {
                    employees: [],
                    confirmed: false,
                  };
                  const isConfirmed = shiftData.confirmed;

                  return (
                    <div
                      key={shift}
                      className={`flex-1 border-t px-3 py-2 transition flex flex-col gap-2 rounded-b-md ${
                        isConfirmed
                          ? "bg-twohas text-white"
                          : "hover:bg-blue-50"
                      }`}
                    >
                      <div className="text-xs font-semibold select-none">
                        {shift === "AM" ? "Morning" : "Night"}
                      </div>

                      <MultiSelectDropdown
                        options={EMPLOYEES}
                        selected={shiftData.employees}
                        onChange={(selected) =>
                          handleChange(dateStr, shift, selected)
                        }
                        disabled={isConfirmed}
                      />

                      {!isConfirmed && (
                        <button
                          onClick={() => handleConfirm(dateStr, shift)}
                          className="mt-1 text-xs bg-twohas text-white rounded px-3 py-1 hover:brightness-110 transition"
                        >
                          Confirm
                        </button>
                      )}
                    </div>
                  );
                })}
            </div>
          );
        })}
      </div>
    </div>
  );
}
