import React, { useState } from "react";

export default function Inventory() {
  const [newInventory, setNewInventory] = useState({
    name: "",
    vendor: "",
    volume: "",
    price: "",
  });

  const handleAdd = () => {
    console.log("New Inventory Added:", newInventory);
    // TODO: add logic to save inventory (API call or state update)
    setNewInventory({ name: "", vendor: "", volume: "", price: "" });
  };

  return (
    <div className="p-6 max-w-full mx-auto space-y-12">
      <button
        onClick={handleAdd}
        disabled={
          !newInventory.name.trim() ||
          !newInventory.vendor.trim() ||
          !newInventory.volume ||
          !newInventory.price
        }
        className="bg-twohas hover:bg-gray-600 text-white px-4 py-2 rounded shadow disabled:opacity-50"
      >
        + Add Inventory
      </button>

      <div className="mb-6 p-4 bg-white rounded shadow grid grid-cols-1 md:grid-cols-4 gap-4">
        <input
          type="text"
          placeholder="Name"
          value={newInventory.name}
          onChange={(e) =>
            setNewInventory({ ...newInventory, name: e.target.value })
          }
          className="border px-3 py-2 rounded w-full"
        />
        <input
          type="text"
          placeholder="Vendor"
          value={newInventory.vendor}
          onChange={(e) =>
            setNewInventory({ ...newInventory, vendor: e.target.value })
          }
          className="border px-3 py-2 rounded w-full"
        />
        <input
          type="text"
          placeholder="Volume / Pack"
          value={newInventory.volume}
          onChange={(e) =>
            setNewInventory({ ...newInventory, volume: e.target.value })
          }
          className="border px-3 py-2 rounded w-full"
        />
        <input
          type="number"
          placeholder="Pack Price"
          value={newInventory.price}
          onChange={(e) =>
            setNewInventory({
              ...newInventory,
              price: e.target.value === "" ? "" : parseFloat(e.target.value),
            })
          }
          className="border px-3 py-2 rounded w-full"
        />
      </div>

      <div className="overflow-auto border rounded">
        <div className="grid grid-cols-5 auto-rows-min border-b">
          <div className="border p-2 font-bold text-center">Item</div>
          <div className="border p-2 font-bold text-center">Vendor</div>
          <div className="border p-2 font-bold text-center">Volume/Pack</div>
          <div className="border p-2 font-bold text-center">Pack Price</div>
          <div className="border p-2 font-bold text-center">Unit Price</div>
        </div>
        {/* TODO: 나중에 map으로 인벤토리 목록 렌더링 */}
      </div>
    </div>
  );
}
