import React, { useState } from "react";
import API from "../../../API/api";

export default function UnavailableDates() {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const [pin, setPin] = useState("");

  const openModal = () => {
    setIsModalOpen(true);
    setStartDate("");
    setEndDate("");
    setPin("");
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const handleConfirm = async () => {
    try {
      if (!startDate || !endDate || pin.length !== 4) {
        return alert("모든 값을 올바르게 입력해주세요.");
      }

      const payload = { startDate, endDate, pin };
      const token = localStorage.getItem("token");

      await API.post("/schedule/unavailableDates", payload, {
        headers: { Authorization: `Bearer ${token}` },
      });

      alert("Unavailable dates saved.");
      setIsModalOpen(false);
    } catch (err) {
      console.error("Failed to save unavailable dates", err);
    }
  };

  return (
    <section>
      <h2 className="text-xl font-bold mb-4 text-twohas">
        Set Unavailable Dates
      </h2>
      <div className="flex gap-6 mb-6">
        <button
          onClick={openModal}
          className="px-6 py-3 text-lg font-semibold bg-twohas text-white rounded-xl shadow-lg hover:bg-twohas/80 transition"
        >
          Add Unavailable
        </button>
      </div>

      {/* 모달 */}
      {isModalOpen && (
        <div className="fixed inset-0 flex items-center justify-center bg-black/50 z-50">
          <div className="bg-white rounded-xl shadow-xl p-6 w-96 text-center">
            <h3 className="text-lg font-semibold text-twohas mb-4">
              Add Unavailable Dates
            </h3>
            <div className="flex flex-col gap-4 mb-4">
              <div>
                <label className="block text-sm font-medium mb-1 text-gray-600">
                  Start Date
                </label>
                <input
                  type="date"
                  value={startDate}
                  onChange={(e) => setStartDate(e.target.value)}
                  className="w-full border px-4 py-2 rounded-lg"
                />
              </div>
              <div>
                <label className="block text-sm font-medium mb-1 text-gray-600">
                  End Date
                </label>
                <input
                  type="date"
                  value={endDate}
                  onChange={(e) => setEndDate(e.target.value)}
                  className="w-full border px-4 py-2 rounded-lg"
                />
              </div>
              <div>
                <label className="block text-sm font-medium mb-1 text-gray-600">
                  Phone Last 4 Digits
                </label>
                <input
                  type="password"
                  maxLength={4}
                  value={pin}
                  onChange={(e) => setPin(e.target.value)}
                  placeholder="1234"
                  className="w-full border px-4 py-2 rounded-lg text-center tracking-widest text-xl"
                />
              </div>
            </div>

            <div className="flex justify-between gap-4">
              <button
                onClick={closeModal}
                className="flex-1 px-4 py-2 bg-gray-300 text-black rounded-lg hover:bg-gray-400"
              >
                Cancel
              </button>
              <button
                onClick={handleConfirm}
                className="flex-1 px-4 py-2 bg-twohas text-white rounded-lg hover:bg-twohas/80"
              >
                Confirm
              </button>
            </div>
          </div>
        </div>
      )}
    </section>
  );
}
