import React, { useEffect, useState } from "react";
import API from "../../API/api";

export default function Employee() {
  const [employees, setEmployees] = useState([]);

  const fetchEmployee = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await API.get("/employee/employeeList", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setEmployees(res.data);
    } catch (error) {
      console.error("Failed", error);
    }
  };

  useEffect(() => {
    fetchEmployee();
  }, []);

  const onClickHandler = async (id) => {
    try {
      const token = localStorage.getItem("token");
      await API.delete("/employee/deleteEmployee", {
        headers: { Authorization: `Bearer ${token}` },
        data: { id },
      });
      await fetchEmployee();
    } catch (error) {
      console.error("Failed", error);
    }
  };

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <div className="flex items-center justify-between mb-6">
        <h2 className="text-4xl font-semibold text-gray-800">Employees</h2>
        <div className="flex gap-2"></div>
      </div>

      <div className="overflow-x-auto rounded-xl shadow-lg">
        <table className="min-w-full divide-y divide-gray-200 bg-white text-sm">
          <thead className="bg-gray-50">
            <tr>
              <th className="px-6 py-4 text-left font-medium text-gray-600">
                #
              </th>
              <th className="px-6 py-4 text-left font-medium text-gray-600">
                Name
              </th>
              <th className="px-6 py-4 text-left font-medium text-gray-600">
                Role
              </th>
              <th className="px-6 py-4 text-left font-medium text-gray-600">
                Hourly Pay
              </th>
              <th className="px-6 py-4 text-left font-medium text-gray-600">
                Phone
              </th>
              <th className="px-6 py-4 text-center font-medium text-gray-600">
                Action
              </th>
            </tr>
          </thead>
          <tbody className="divide-y divide-gray-100 text-gray-700">
            {employees.map((emp, index) => (
              <tr key={emp._id} className="hover:bg-gray-50 transition">
                <td className="px-6 py-4">{index + 1}</td>
                <td className="px-6 py-4 font-medium">{emp.name}</td>
                <td className="px-6 py-4">{emp.role}</td>
                <td className="px-6 py-4">${emp.hourlyPay.toFixed(2)}</td>
                <td className="px-6 py-4">{emp.number}</td>
                <td className="px-6 py-4 text-center">
                  <button
                    onClick={() => onClickHandler(emp._id)}
                    className="inline-block bg-twohas hover:bg-twohas text-white text-xs font-semibold px-4 py-2 rounded-full shadow-sm transition"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
