import React, { useState, useEffect } from "react";
import API from "../../API/api";

export default function CPAReport() {
  const [employees, setEmployees] = useState([]);
  const [dates, setDates] = useState([]);
  const [scheduleData, setScheduleData] = useState({});

  useEffect(() => {
    const fetchEmployees = async () => {
      try {
        const token = localStorage.getItem("token");
        const res = await API.get("/employee/employeeList", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setEmployees(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchEmployees();
  }, []);

  const generateDateRange = (start, end) => {
    const result = [];
    let current = new Date(start);
    const endDate = new Date(end);
    while (current <= endDate) {
      result.push(new Date(current));
      current.setDate(current.getDate() + 1);
    }
    return result;
  };

  useEffect(() => {
    setDates(generateDateRange("2025-08-18", "2025-08-22"));
  }, []);

  return (
    <div className="p-6 max-w-full overflow-auto">
      <h2 className="text-3xl font-bold mb-6 text-center text-gray-800">
        CPA Report
      </h2>
      <div className="overflow-x-auto">
        <table className="min-w-max w-full border-collapse">
          <thead className="sticky top-0 bg-gray-100 shadow-md z-10">
            <tr>
              <th
                rowSpan={3}
                className="border px-3 py-2 bg-gray-200 text-left text-sm font-semibold text-gray-700"
              >
                Name
              </th>
              <th
                rowSpan={3}
                className="border px-3 py-2 bg-gray-200 text-left text-sm font-semibold text-gray-700"
              >
                Hourly Pay
              </th>
              {dates.map((date) => (
                <th
                  key={date}
                  colSpan={4}
                  className="border px-3 py-2 bg-gray-50 text-center text-sm font-semibold text-gray-600"
                >
                  {date.getMonth() + 1}/{date.getDate()}
                </th>
              ))}
            </tr>
            <tr>
              {dates.map((date) => (
                <React.Fragment key={date}>
                  <th
                    colSpan={2}
                    className="border px-2 py-1 bg-gray-50 text-center text-xs font-medium"
                  >
                    AM
                  </th>
                  <th
                    colSpan={2}
                    className="border px-2 py-1 bg-gray-50 text-center text-xs font-medium"
                  >
                    PM
                  </th>
                </React.Fragment>
              ))}
            </tr>
            <tr>
              {dates.map((date) => (
                <React.Fragment key={date}>
                  <th className="border px-2 py-1 bg-gray-50 text-center text-xs">
                    Hourly
                  </th>
                  <th className="border px-2 py-1 bg-gray-50 text-center text-xs">
                    Tip
                  </th>
                  <th className="border px-2 py-1 bg-gray-50 text-center text-xs">
                    Hourly
                  </th>
                  <th className="border px-2 py-1 bg-gray-50 text-center text-xs">
                    Tip
                  </th>
                </React.Fragment>
              ))}
            </tr>
          </thead>
          <tbody>
            {employees.map((emp) => (
              <tr key={emp._id} className="hover:bg-gray-50 transition-colors">
                <td className="border px-3 py-2 font-medium text-gray-800">
                  {emp.name}
                </td>
                <td className="border px-3 py-2 text-gray-700">
                  ${emp.hourlyPay}
                </td>
                {dates.map((date) => {
                  const key = `${date.getMonth() + 1}/${date.getDate()}`;
                  const dayData = scheduleData[emp._id]?.[key] || {};
                  const AM = dayData.AM || { hourly: 0, tip: 0 };
                  const PM = dayData.PM || { hourly: 0, tip: 0 };
                  return (
                    <React.Fragment key={key}>
                      <td className="border px-2 py-1 text-right">
                        {AM.hourly}
                      </td>
                      <td className="border px-2 py-1 text-right">{AM.tip}</td>
                      <td className="border px-2 py-1 text-right">
                        {PM.hourly}
                      </td>
                      <td className="border px-2 py-1 text-right">{PM.tip}</td>
                    </React.Fragment>
                  );
                })}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
