import React, { useEffect, useState } from "react";
import API from "../../../API/api";

const formatTime = (timestamp) => {
  const date = new Date(timestamp);
  const datePart = date.toLocaleDateString(undefined, {
    month: "2-digit",
    day: "2-digit",
  });
  const timePart = date.toLocaleTimeString(undefined, {
    hour: "2-digit",
    minute: "2-digit",
  });
  return `${datePart} ${timePart}`;
};

export default function UserCashCount({ workday }) {
  const [cashCounts, setCashCounts] = useState([]);

  const fetchCashCounts = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await API.get("/summary/cashCountList", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setCashCounts(res.data);
    } catch (err) {
      console.error("Failed to fetch cash counts", err);
    }
  };

  const filterWorkday = (date) =>
    new Date(date).toLocaleDateString() ===
    new Date(workday).toLocaleDateString();

  useEffect(() => {
    fetchCashCounts();
  }, []);

  return (
    <section>
      <h2 className="text-xl font-bold mb-4 text-twohas">Cash Count</h2>
      <table className="w-full border-collapse border border-gray-200 text-sm rounded-lg overflow-hidden shadow">
        <thead className="bg-gray-100">
          <tr>
            <th className="border px-4 py-2">Total</th>
            <th className="border px-4 py-2">Bills</th>
            <th className="border px-4 py-2">Coins</th>
            <th className="border px-4 py-2">Time</th>
          </tr>
        </thead>
        <tbody>
          {cashCounts
            .filter((count) =>
              filterWorkday(count.timestamp || count.createdAt)
            )
            .map((count) => (
              <tr key={count.id} className="odd:bg-white even:bg-gray-50">
                <td className="border px-4 py-2">${count.totalAmount}</td>
                <td className="border px-4 py-2">
                  {Object.entries(count.bills || {})
                    .filter(([_, quantity]) => quantity > 0)
                    .map(
                      ([denomination, quantity]) =>
                        `${denomination}x${quantity}`
                    )
                    .join(", ")}
                </td>
                <td className="border px-4 py-2">
                  {Object.entries(count.coins || {})
                    .filter(([_, quantity]) => quantity > 0)
                    .map(
                      ([denomination, quantity]) =>
                        `${denomination}x${quantity}`
                    )
                    .join(", ")}
                </td>
                <td className="border px-4 py-2">
                  {formatTime(count.timestamp || count.createdAt)}
                </td>
              </tr>
            ))}
        </tbody>
      </table>
    </section>
  );
}
