// CPAEmployeeGrid.js
import React from "react";

export default function CPAEmployeeGrid({ employees, scheduleData, dates }) {
  return employees.map((emp) => (
    <div key={emp._id} className="overflow-x-auto mb-6 border rounded shadow">
      <div className="grid grid-cols-[150px_80px_repeat(auto-fit,minmax(80px,1fr))]">
        <div className="border px-2 py-1 font-bold bg-gray-100 sticky left-0 z-10">
          {emp.name}
        </div>
        <div className="border px-2 py-1 font-bold bg-gray-100 sticky left-[150px] z-10">
          {emp.hourlyPay}
        </div>

        {dates.map((d) => (
          <div
            key={`date-${d}`}
            className="border px-1 py-1 text-center font-bold bg-gray-200"
          >
            {d}
          </div>
        ))}

        {dates.map((d) => (
          <React.Fragment key={`ampm-${d}`}>
            <div className="border px-1 py-1 text-center">AM</div>
            <div className="border px-1 py-1 text-center">PM</div>
          </React.Fragment>
        ))}

        {dates.map((d) => {
          const am = scheduleData[d]?.AM || {};
          const pm = scheduleData[d]?.PM || {};
          return (
            <React.Fragment key={`values-${d}`}>
              <div className="border px-1 py-1 grid grid-cols-2 text-center">
                <span>{am.hourly}</span>
                <span>{am.tip}</span>
              </div>
              <div className="border px-1 py-1 grid grid-cols-2 text-center">
                <span>{pm.hourly}</span>
                <span>{pm.tip}</span>
              </div>
            </React.Fragment>
          );
        })}
      </div>
    </div>
  ));
}
