import React, { useState } from "react";

const generateWeekDates = (startDate) => {
  const week = [];
  for (let i = 0; i < 7; i++) {
    const d = new Date(startDate);
    d.setDate(d.getDate() + i);
    week.push(d);
  }
  return week;
};

const today = new Date([]);
const todayDay = today.getDay();
const diffToMonday = todayDay === 0 ? -6 : 1 - todayDay;

const startDateObj = new Date(today);
startDateObj.setDate(today.getDate() + diffToMonday);
export default function ScheduleTableByEmp({ employeesList, schedule }) {
  const [weekDates, setWeekDates] = useState(() =>
    generateWeekDates(startDateObj)
  );
  console.log(weekDates);
  const colTemplate = [
    "minmax(140px, 1fr)",
    ...Array(weekDates.length * 2).fill("minmax(70px, 1fr)"),
  ].join(" ");

  return (
    <div className="overflow-auto border rounded-lg shadow-md bg-white">
      <div className="flex justify-center items-center gap-4 mb-6">
        <button
          onClick={() => {
            const lastweek = new Date();
            lastweek.setDate(startDateObj - 7);
            setWeekDates(generateWeekDates(lastweek));
          }}
          className="px-3 py-1 bg-gray-200 rounded"
        >
          ◀ Last Week
        </button>
        <button
          onClick={() => {
            const lastweek = new Date();
            lastweek.setDate(startDateObj - 7);
            setWeekDates(generateWeekDates(lastweek));
          }}
          className="px-3 py-1 bg-gray-200 rounded"
        >
          Next Week ▶
        </button>
      </div>
      <div
        className="grid border-b border-gray-200 bg-gray-50 sticky top-0 z-10"
        style={{ gridTemplateColumns: colTemplate }}
      >
        <div className="border p-3 text-center font-semibold row-span-2 bg-gray-100">
          Name
        </div>

        {weekDates.map((date) => (
          <div
            key={date.toDateString()}
            className="border p-2 text-center font-semibold col-span-2 text-gray-700"
          >
            {`${date.getMonth() + 1}/${date.getDate()}`}
          </div>
        ))}

        {weekDates.map((date) => (
          <React.Fragment key={date.toDateString() + "-shift"}>
            <div className="border text-center text-xs text-gray-500">Open</div>
            <div className="border text-center text-xs text-gray-500">
              Close
            </div>
          </React.Fragment>
        ))}
      </div>

      {employeesList &&
        employeesList.map((emp) => (
          <div
            key={emp._id}
            className="grid auto-rows-min border-b border-gray-200 hover:bg-gray-50 transition-colors"
            style={{ gridTemplateColumns: colTemplate }}
          >
            <div className="border p-3 text-center font-medium text-gray-800">
              {emp.name}
            </div>

            {weekDates.map((date) => {
              const dateStr = `${date.getMonth() + 1}/${date.getDate()}`;
              const dayData = schedule[dateStr] || {};
              const shifts = ["AM", "PM"];

              return shifts.map((shift) => {
                const shiftData = dayData[shift] || { employees: [] };
                const isScheduled = shiftData.employees.some(
                  (e) => e._id === emp._id
                );

                return (
                  <div
                    key={emp._id + "-" + dateStr + "-" + shift}
                    className="border flex items-center justify-center h-18"
                  >
                    <span
                      className={`w-6 h-6 rounded-full flex items-center justify-center text-white font-bold 
                        ${isScheduled ? "bg-green-500" : "bg-gray-300"}`}
                    >
                      {isScheduled ? "✓" : ""}
                    </span>
                  </div>
                );
              });
            })}
          </div>
        ))}
    </div>
  );
}
