import React, { useState, useEffect } from "react";
import API from "../../API/api";
import CPAEmployeeGrid from "./CPAComponents/CPAEmployeeGrid";

export default function CPA() {
  const [employees, setEmployees] = useState([]);
  const [selectedMonth, setSelectedMonth] = useState("current");
  const [selectedRange, setSelectedRange] = useState("1-15");
  const [scheduleData, setScheduleData] = useState({});
  const [dateRange, setDateRange] = useState([]);

  useEffect(() => {
    const fetchEmployees = async () => {
      try {
        const token = localStorage.getItem("token");
        const res = await API.get("/employee/employeeList", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setEmployees(res.data);

        const scheduleRes = await API.get("/schedule/tipList", {
          headers: { Authorization: `Bearer ${token}` },
        });

        const mappedSchedule = {};
        scheduleRes.data.forEach((item) => {
          const dateObj = new Date(item.date);
          const key = `${dateObj.getMonth() + 1}/${dateObj.getDate()}`;
          const empId = item.employee?._id || item.employeeId;
          mappedSchedule[empId] = mappedSchedule[empId] || {};
          mappedSchedule[empId][key] = {
            AM: { hourly: item.AM?.hourly || 0, tip: item.AM?.tipTotal || 0 },
            PM: { hourly: item.PM?.hourly || 0, tip: item.PM?.tipTotal || 0 },
          };
        });

        setScheduleData(mappedSchedule);
      } catch (err) {
        console.error(err);
      }
    };
    fetchEmployees();
  }, []);

  useEffect(() => {
    const today = new Date();
    let year = today.getFullYear();
    let month =
      selectedMonth === "current" ? today.getMonth() : today.getMonth() - 1;

    const startDay = selectedRange === "1-15" ? 1 : 16;
    const endDay =
      selectedRange === "1-15" ? 15 : new Date(year, month + 1, 0).getDate();

    const dates = [];
    for (let d = startDay; d <= endDay; d++)
      dates.push(new Date(year, month, d));
    setDateRange(dates);
  }, [selectedMonth, selectedRange]);

  return (
    <div className="p-6 max-w-full mx-auto">
      <h2 className="text-3xl font-bold mb-6 text-center">CPA</h2>

      <div className="flex gap-4 mb-6">
        <select
          value={selectedMonth}
          onChange={(e) => setSelectedMonth(e.target.value)}
          className="border rounded px-3 py-1"
        >
          <option value="current">This Month</option>
          <option value="previous">Previous Month</option>
        </select>

        <select
          value={selectedRange}
          onChange={(e) => setSelectedRange(e.target.value)}
          className="border rounded px-3 py-1"
        >
          <option value="1-15">1-15</option>
          <option value="16-end">16-End</option>
        </select>
      </div>

      <CPAEmployeeGrid
        employees={employees}
        scheduleData={scheduleData}
        dateRange={dateRange}
      />
    </div>
  );
}
