import React, { useEffect, useState } from "react";
import API from "../../API/api";
import InventoryTable from "../InventoryComponents/InventoryTable";

export default function Inventory() {
  const [newInventory, setNewInventory] = useState({
    name: "",
    vendor: "",
    volume: "",
    price: "",
    inStock: "",
  });

  const [inventoryList, setInventoryList] = useState([]);
  useEffect(() => {
    fetchInventory();
  }, []);

  const calcUnitPrice = (price, volume) => {
    if (!price || !volume) return "-";
    const match = String(volume).match(/\d+/);
    if (!match) return "-";
    const qty = parseInt(match[0], 10);
    return `$${(price / qty).toFixed(2)}`;
  };
  const fetchInventory = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await API.get("/inventory/inventoryList", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setInventoryList(res.data);
    } catch (error) {
      console.error("Failed", error);
    }
  };

  const handleAddInventory = async () => {
    const exists = inventoryList.some(
      (item) =>
        item.name.toLowerCase() === newInventory.name.toLowerCase() &&
        item.vendor.toLowerCase() === newInventory.vendor.toLowerCase()
    );

    if (exists) {
      alert("Item Already Exist");
      return;
    }
    try {
      const token = localStorage.getItem("token");
      await API.post("/inventory/addInventory", newInventory, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setNewInventory({
        name: "",
        vendor: "",
        volume: "",
        price: "",
        inStock: "",
      });
      await fetchInventory();
    } catch (error) {
      console.error("Failed to add", error);
    }
  };

  return (
    <div className="p-8 max-w-6xl mx-auto space-y-10 bg-gray-50 min-h-screen">
      <div className="flex items-center justify-between">
        <h1 className="text-3xl font-bold text-gray-800">
          Inventory Management
        </h1>
        <button
          onClick={handleAddInventory}
          disabled={
            !newInventory.name.trim() ||
            !newInventory.vendor.trim() ||
            !newInventory.volume ||
            !newInventory.price ||
            !newInventory.inStock
          }
          className="bg-blue-600 hover:bg-blue-700 disabled:bg-gray-400 disabled:cursor-not-allowed text-white px-6 py-2 rounded-lg shadow transition-colors"
        >
          + Add Inventory
        </button>
      </div>

      <div className="bg-white p-6 rounded-xl shadow space-y-4">
        <h2 className="text-xl font-semibold text-gray-700">New Item</h2>
        <div className="grid grid-cols-1 md:grid-cols-5 gap-6">
          <div>
            <label className="block text-sm font-medium text-gray-600 mb-1">
              Item Name
            </label>
            <input
              type="text"
              placeholder="Ex: Coke"
              value={newInventory.name}
              onChange={(e) =>
                setNewInventory({ ...newInventory, name: e.target.value })
              }
              className="border px-3 py-2 rounded w-full focus:ring-2 focus:ring-blue-400 focus:outline-none"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-600 mb-1">
              Vendor
            </label>
            <input
              type="text"
              placeholder="Ex: Costco"
              value={newInventory.vendor}
              onChange={(e) =>
                setNewInventory({ ...newInventory, vendor: e.target.value })
              }
              className="border px-3 py-2 rounded w-full focus:ring-2 focus:ring-blue-400 focus:outline-none"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-600 mb-1">
              Volume / Pack
            </label>
            <input
              type="text"
              placeholder="Ex: 24 cans"
              value={newInventory.volume}
              onChange={(e) =>
                setNewInventory({ ...newInventory, volume: e.target.value })
              }
              className="border px-3 py-2 rounded w-full focus:ring-2 focus:ring-blue-400 focus:outline-none"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-600 mb-1">
              Pack Price
            </label>
            <input
              type="number"
              placeholder="Ex: 19.99"
              value={newInventory.price}
              onChange={(e) =>
                setNewInventory({
                  ...newInventory,
                  price:
                    e.target.value === "" ? "" : parseFloat(e.target.value),
                })
              }
              className="border px-3 py-2 rounded w-full focus:ring-2 focus:ring-blue-400 focus:outline-none"
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-600 mb-1">
              Quantity
            </label>
            <input
              type="text"
              placeholder="Ex: 24"
              value={newInventory.inStock}
              onChange={(e) =>
                setNewInventory({ ...newInventory, inStock: e.target.value })
              }
              className="border px-3 py-2 rounded w-full focus:ring-2 focus:ring-blue-400 focus:outline-none"
            />
          </div>
        </div>
      </div>
      <InventoryTable
        inventoryList={inventoryList}
        calcUnitPrice={calcUnitPrice}
      />
    </div>
  );
}
