import React from "react";
import UserInventoryForm from "./InventoryComponents/UserInventoryForm";
import UserInventoryTable from "./InventoryComponents/UserInventoryTable";
import { useState, useEffect } from "react";
import API from "../../API/api";

export default function UserInventory() {
  const [newInventory, setNewInventory] = useState({
    name: "",
    inStock: "",
  });
  const [inventoryList, setInventoryList] = useState([]);
  const [isOn, setIsOn] = useState(false);

  useEffect(() => {
    fetchInventory();
  }, []);

  const fetchInventory = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await API.get("/inventory/inventoryList", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setInventoryList(res.data);
    } catch (error) {
      console.error("Failed", error);
    }
  };

  const handleAddInventory = async () => {
    const exists = inventoryList.some(
      (item) =>
        item.name.toLowerCase() === newInventory.name.toLowerCase() &&
        item.vendor.toLowerCase() === newInventory.vendor.toLowerCase()
    );

    if (exists) {
      alert("Item Already Exist");
      return;
    }
    try {
      const token = localStorage.getItem("token");
      await API.post("/inventory/addInventory", newInventory, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setNewInventory({
        name: "",
        updatesByEmp: false,
      });
      await fetchInventory();
    } catch (error) {
      console.error("Failed to add", error);
    }
  };

  return (
    <div className="p-8 max-w-6xl mx-auto space-y-10 bg-gray-50 min-h-screen">
      <UserInventoryForm
        newInventory={newInventory}
        setNewInventory={setNewInventory}
        handleAddInventory={handleAddInventory}
        isOn={isOn}
        setIsOn={setIsOn}
      />

      {inventoryList.map((items, index) => (
        <div key={index}>
          <UserInventoryTable
            inventoryList={items}
            fetchInventory={fetchInventory}
          />
        </div>
      ))}
    </div>
  );
}
