import React from "react";

const generateWeekDates = (startDate) => {
  const week = [];
  for (let i = 0; i < 7; i++) {
    const d = new Date(startDate);
    d.setDate(d.getDate() + i);
    week.push(d);
  }
  return week;
};

export default function ScheduleTableByEmp({ employeesList, schedule }) {
  const today = new Date();
  const todayDay = today.getDay();
  const diffToMonday = todayDay === 0 ? -6 : 1 - todayDay;

  const startDateObj = new Date(today);
  startDateObj.setDate(today.getDate() + diffToMonday);
  const weekDates = generateWeekDates(startDateObj);

  const colTemplate = [
    "minmax(120px, 1fr)",
    ...Array(weekDates.length * 2).fill("minmax(60px, 1fr)"),
  ].join(" ");
  console.log(schedule);
  return (
    <div className="overflow-auto border rounded">
      <div
        className="grid border-b auto-rows-min"
        style={{ gridTemplateColumns: colTemplate }}
      >
        <div className="border p-2 text-center font-bold row-span-2">Name</div>

        {weekDates.map((date) => (
          <div
            key={date.toDateString()}
            className="border p-1 text-center font-semibold col-span-2"
          >
            {`${date.getMonth() + 1}/${date.getDate()}`}
          </div>
        ))}

        {weekDates.map((date) => (
          <React.Fragment key={date.toDateString() + "-shift"}>
            <div className="border text-center text-xs">Open</div>
            <div className="border text-center text-xs">Close</div>
          </React.Fragment>
        ))}
      </div>

      {employeesList &&
        employeesList.map((emp) => (
          <div
            key={emp._id}
            className="grid border-t auto-rows-min"
            style={{ gridTemplateColumns: colTemplate }}
          >
            <div className="border p-2 text-center">{emp.name}</div>

            {weekDates.map((date) => {
              const dateStr = `${date.getMonth() + 1}/${date.getDate()}`;
              const dayData = schedule[dateStr] || {};
              const shifts = ["AM", "PM"];

              return shifts.map((shift) => {
                const shiftData = dayData[shift] || { employees: [] };
                const isScheduled = shiftData.employees.some(
                  (e) => e._id === emp._id
                );

                return (
                  <div
                    key={emp._id + "-" + dateStr + "-" + shift}
                    className="border p-1 text-center h-12 flex items-center justify-center"
                  >
                    {isScheduled ? "✅" : "⚪"}
                  </div>
                );
              });
            })}
          </div>
        ))}
    </div>
  );
}
