import React from "react";

export default function Schedule() {
  const today = new Date();
  const year = today.getFullYear();
  const month = today.getMonth(); // 0-indexed (0 = January)

  const firstDay = new Date(year, month, 1).getDay();
  const lastDate = new Date(year, month + 1, 0).getDate();

  const calendarDays = [];
  for (let i = 0; i < firstDay; i++) calendarDays.push(null);
  for (let i = 1; i <= lastDate; i++) calendarDays.push(i);

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">
        {year}년 {month + 1}월 스케줄
      </h2>

      {/* 요일 헤더 */}
      <div className="grid grid-cols-7 gap-2 text-center text-sm font-semibold text-gray-600 mb-2">
        {["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"].map((d) => (
          <div key={d} className="py-2 bg-gray-100 rounded">
            {d}
          </div>
        ))}
      </div>

      {/* 날짜 셀 */}
      <div className="grid grid-cols-7 gap-2 text-sm">
        {calendarDays.map((day, index) => (
          <div
            key={index}
            className="h-32 border rounded-lg flex flex-col justify-between overflow-hidden bg-white shadow-sm"
          >
            {/* 날짜 숫자 */}
            <div className="px-2 pt-1 text-right text-xs text-gray-500">
              {day}
            </div>

            {/* 오전 */}
            <div className="flex-1 border-t border-gray-200 px-2 py-1 hover:bg-blue-50 transition">
              <div className="text-xs font-medium text-gray-600">오전</div>
              <select className="w-full text-xs mt-1 bg-gray-50 border rounded">
                <option>직원 선택</option>
                <option>홍길동</option>
                <option>김민수</option>
                {/* DB 연동 시 동적으로 넣을 수 있음 */}
              </select>
            </div>

            {/* 오후 */}
            <div className="flex-1 border-t border-gray-200 px-2 py-1 hover:bg-blue-50 transition">
              <div className="text-xs font-medium text-gray-600">오후</div>
              <select className="w-full text-xs mt-1 bg-gray-50 border rounded">
                <option>직원 선택</option>
                <option>홍길동</option>
                <option>김민수</option>
              </select>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
