import React, { useState, useEffect } from "react";
import API from "../../API/api";

export default function CPA() {
  const [employees, setEmployees] = useState([]);
  const [dates, setDates] = useState([]);
  const [data, setData] = useState({});
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");

  useEffect(() => {
    const fetchEmployees = async () => {
      try {
        const token = localStorage.getItem("token");
        const res = await API.get("/employee/employeeList", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setEmployees(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchEmployees();
  }, []);

  const handleDateChange = () => {
    if (!startDate || !endDate) return;
    const start = new Date(startDate);
    const end = new Date(endDate);
    const tempDates = [];
    for (let d = new Date(start); d <= end; d.setDate(d.getDate() + 1)) {
      tempDates.push(new Date(d));
    }
    setDates(tempDates);
  };

  const chunkDates = (datesArray, size = 15) => {
    const chunks = [];
    for (let i = 0; i < datesArray.length; i += size) {
      chunks.push(datesArray.slice(i, i + size));
    }
    return chunks;
  };

  const handleChange = (empId, dateStr, shift, type, value) => {
    setData((prev) => ({
      ...prev,
      [empId]: {
        ...prev[empId],
        [dateStr]: {
          ...prev[empId]?.[dateStr],
          [shift]: {
            ...prev[empId]?.[dateStr]?.[shift],
            [type]: value,
          },
        },
      },
    }));
  };

  return (
    <div className="p-6 max-w-7xl mx-auto">
      <h2 className="text-3xl font-bold mb-6 text-center">CPA Dashboard</h2>
      <div className="flex gap-4 mb-4">
        <div>
          <label className="block text-sm font-semibold">Start Date</label>
          <input
            type="date"
            value={startDate}
            onChange={(e) => setStartDate(e.target.value)}
            className="border rounded px-2 py-1"
          />
        </div>
        <div>
          <label className="block text-sm font-semibold">End Date</label>
          <input
            type="date"
            value={endDate}
            onChange={(e) => setEndDate(e.target.value)}
            className="border rounded px-2 py-1"
          />
        </div>
        <button
          onClick={handleDateChange}
          className="bg-blue-500 text-white px-4 py-1 rounded mt-6"
        >
          Apply
        </button>
      </div>

      {employees.map((emp) => {
        const empData = data[emp._id] || {};
        const dateChunks = chunkDates(dates, 15);

        return dateChunks.map((chunk, idx) => (
          <table
            key={`${emp._id}-${idx}`}
            className="min-w-max w-full border-collapse mb-6 text-xs"
          >
            <thead className="bg-gray-100">
              <tr>
                <th
                  rowSpan={3}
                  className="border px-3 py-2 bg-gray-200 text-left"
                >
                  {idx === 0 ? emp.name : ""}
                </th>
                <th
                  rowSpan={3}
                  className="border px-3 py-2 bg-gray-200 text-left"
                >
                  {idx === 0 ? `$${emp.hourlyPay}` : ""}
                </th>
                {chunk.map((date) => (
                  <th
                    key={date}
                    colSpan={4}
                    className="border px-2 py-1 text-center bg-gray-50"
                  >
                    {date.getMonth() + 1}/{date.getDate()}
                  </th>
                ))}
              </tr>
              <tr>
                {chunk.map((date) => (
                  <React.Fragment key={`am-pm-${date}`}>
                    <th colSpan={2} className="border px-1 py-1 text-center">
                      AM
                    </th>
                    <th colSpan={2} className="border px-1 py-1 text-center">
                      PM
                    </th>
                  </React.Fragment>
                ))}
              </tr>
              <tr>
                {chunk.map((date) => (
                  <React.Fragment key={`hour-tip-${date}`}>
                    <th className="border px-1 py-1 text-center">Hourly</th>
                    <th className="border px-1 py-1 text-center">Tip</th>
                    <th className="border px-1 py-1 text-center">Hourly</th>
                    <th className="border px-1 py-1 text-center">Tip</th>
                  </React.Fragment>
                ))}
              </tr>
            </thead>
            <tbody>
              <tr>
                {chunk.map((date) => {
                  const dateStr = `${date.getMonth() + 1}/${date.getDate()}`;
                  const day = empData[dateStr] || {};
                  const am = day.AM || {};
                  const pm = day.PM || {};
                  return (
                    <React.Fragment key={`values-${date}`}>
                      <td className="border px-1 py-1">
                        <input
                          type="number"
                          value={am.hourly || ""}
                          onChange={(e) =>
                            handleChange(
                              emp._id,
                              dateStr,
                              "AM",
                              "hourly",
                              e.target.value
                            )
                          }
                          className="w-full border rounded px-1 py-0.5 text-right text-xs"
                        />
                      </td>
                      <td className="border px-1 py-1">
                        <input
                          type="number"
                          value={am.tip || ""}
                          onChange={(e) =>
                            handleChange(
                              emp._id,
                              dateStr,
                              "AM",
                              "tip",
                              e.target.value
                            )
                          }
                          className="w-full border rounded px-1 py-0.5 text-right text-xs"
                        />
                      </td>
                      <td className="border px-1 py-1">
                        <input
                          type="number"
                          value={pm.hourly || ""}
                          onChange={(e) =>
                            handleChange(
                              emp._id,
                              dateStr,
                              "PM",
                              "hourly",
                              e.target.value
                            )
                          }
                          className="w-full border rounded px-1 py-0.5 text-right text-xs"
                        />
                      </td>
                      <td className="border px-1 py-1">
                        <input
                          type="number"
                          value={pm.tip || ""}
                          onChange={(e) =>
                            handleChange(
                              emp._id,
                              dateStr,
                              "PM",
                              "tip",
                              e.target.value
                            )
                          }
                          className="w-full border rounded px-1 py-0.5 text-right text-xs"
                        />
                      </td>
                    </React.Fragment>
                  );
                })}
              </tr>
            </tbody>
          </table>
        ));
      })}
    </div>
  );
}
