import React, { useState } from "react";

export default function Tv() {
  const [ticketNumber, setTicketNumber] = useState("");
  const [guestName, setGuestName] = useState("");
  const [cards, setCards] = useState([]);

  const handleAddCard = () => {
    if (!ticketNumber || !guestName) return;
    setCards([...cards, { ticket: ticketNumber, guest: guestName }]);
    setTicketNumber("");
    setGuestName("");
  };

  return (
    <div className="p-6 min-h-screen bg-gray-100 flex flex-col gap-6">
      <div className="bg-white p-4 rounded-xl shadow-md flex items-center gap-4">
        <input
          type="text"
          placeholder="Ticket #"
          value={ticketNumber}
          onChange={(e) => setTicketNumber(e.target.value)}
          className="border px-3 py-1 rounded-md focus:outline-none focus:ring-2 focus:ring-twohas w-32"
        />
        <input
          type="text"
          placeholder="Guest Name"
          value={guestName}
          onChange={(e) => setGuestName(e.target.value)}
          className="border px-3 py-1 rounded-md focus:outline-none focus:ring-2 focus:ring-twohas w-48"
        />
        <button
          onClick={handleAddCard}
          className="bg-twohas text-white px-4 py-1 rounded-md hover:bg-twohas/80 transition"
        >
          Add
        </button>
      </div>

      <div className="bg-white p-4 rounded-xl shadow-md min-h-[300px]">
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-4">
          {cards.map((card, idx) => (
            <div
              key={idx}
              className="bg-twohas text-white rounded-xl shadow-lg p-4 flex flex-col justify-between hover:scale-105 transition-transform"
            >
              <div className="text-sm font-semibold">Ticket #</div>
              <div className="text-lg font-bold mb-2">K{card.ticket}</div>
              <div className="text-sm font-semibold">Guest Name</div>
              <div className="text-md">{card.guest}</div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
