import React, { useState, useRef, useEffect } from "react";

const EMPLOYEES = ["홍길동", "김민수", "최유진", "박서준"];

function MultiSelectDropdown({ options, selected, onChange, disabled }) {
  const [open, setOpen] = useState(false);
  const containerRef = useRef();

  useEffect(() => {
    function handleClickOutside(event) {
      if (
        containerRef.current &&
        !containerRef.current.contains(event.target)
      ) {
        setOpen(false);
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  const toggleOption = (option) => {
    if (disabled) return;
    if (selected.includes(option)) {
      onChange(selected.filter((o) => o !== option));
    } else {
      onChange([...selected, option]);
    }
  };

  return (
    <div className="relative" ref={containerRef}>
      <button
        type="button"
        onClick={() => !disabled && setOpen((o) => !o)}
        className={`w-full border rounded-md px-3 py-1.5 text-left text-sm font-medium transition-colors
          ${
            disabled
              ? "bg-twohas text-white cursor-not-allowed border-twohas"
              : "bg-white text-gray-700 border-gray-300 hover:border-twohas"
          }`}
      >
        {selected.length === 0 ? (
          <span className="text-gray-400">직원 선택</span>
        ) : (
          <span className="truncate" title={selected.join(", ")}>
            {selected.join(", ")}
          </span>
        )}
      </button>

      {open && !disabled && (
        <div className="absolute z-20 mt-1 w-full max-h-48 overflow-auto rounded-md border border-gray-300 bg-white shadow-lg text-sm">
          {options.map((option) => (
            <label
              key={option}
              className="flex items-center gap-3 px-3 py-2 cursor-pointer hover:bg-gray-100"
            >
              <input
                type="checkbox"
                checked={selected.includes(option)}
                onChange={() => toggleOption(option)}
                className="cursor-pointer accent-twohas"
              />
              <span className="select-none">{option}</span>
            </label>
          ))}
        </div>
      )}
    </div>
  );
}

export default function Schedule() {
  const today = new Date();
  const year = today.getFullYear();
  const month = today.getMonth();

  const firstDay = new Date(year, month, 1).getDay();
  const lastDate = new Date(year, month + 1, 0).getDate();

  const calendarDays = [];
  for (let i = 0; i < firstDay; i++) calendarDays.push(null);
  for (let i = 1; i <= lastDate; i++) calendarDays.push(i);

  const [schedule, setSchedule] = useState({});

  const handleChange = (dateStr, shift, selectedEmployees) => {
    setSchedule((prev) => {
      const dayData = prev[dateStr] || {
        AM: { employees: [], confirmed: false },
        PM: { employees: [], confirmed: false },
      };
      return {
        ...prev,
        [dateStr]: {
          ...dayData,
          [shift]: {
            ...dayData[shift],
            employees: selectedEmployees,
          },
        },
      };
    });
  };

  const handleConfirm = (dateStr, shift) => {
    setSchedule((prev) => {
      const dayData = prev[dateStr] || {};
      return {
        ...prev,
        [dateStr]: {
          ...dayData,
          [shift]: {
            ...(dayData[shift] || { employees: [] }),
            confirmed: true,
          },
        },
      };
    });
  };

  return (
    <div className="p-6 max-w-6xl mx-auto">
      <h2 className="text-3xl font-extrabold mb-6 text-gray-900">
        {year}년 {month + 1}월 스케줄
      </h2>

      {/* 요일 헤더 */}
      <div className="grid grid-cols-7 gap-3 text-center text-sm font-semibold text-gray-700 mb-4 select-none">
        {["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"].map((d) => (
          <div
            key={d}
            className="py-3 rounded-md bg-gray-100 border border-gray-300 shadow-inner"
          >
            {d}
          </div>
        ))}
      </div>

      {/* 날짜 셀 */}
      <div className="grid grid-cols-7 gap-4 text-sm">
        {calendarDays.map((day, index) => {
          const dateStr = day
            ? `${year}-${String(month + 1).padStart(2, "0")}-${String(
                day
              ).padStart(2, "0")}`
            : null;
          const dayData = schedule[dateStr] || {};

          return (
            <div
              key={index}
              className={`h-48 border rounded-xl bg-white shadow-md flex flex-col justify-between
                ${
                  day
                    ? "hover:shadow-lg transition-shadow"
                    : "bg-transparent border-none"
                }`}
            >
              {/* 날짜 번호 */}
              <div className="px-3 pt-2 text-right text-xs font-semibold text-gray-400 select-none">
                {day || ""}
              </div>

              {/* 오전/오후 섹션 */}
              {day &&
                ["AM", "PM"].map((shift) => {
                  const shiftData = dayData[shift] || {
                    employees: [],
                    confirmed: false,
                  };
                  const isConfirmed = shiftData.confirmed;

                  return (
                    <div
                      key={shift}
                      className={`flex-1 border-t px-4 py-2 flex flex-col gap-1 rounded-b-lg
                      ${
                        isConfirmed
                          ? "bg-twohas text-white border-twohas"
                          : "bg-gray-50 border-gray-200 hover:bg-blue-50"
                      } transition-colors duration-200`}
                    >
                      <div className="text-xs font-semibold select-none">
                        {shift === "AM" ? "오전" : "오후"}
                      </div>

                      <MultiSelectDropdown
                        options={EMPLOYEES}
                        selected={shiftData.employees}
                        onChange={(selected) =>
                          handleChange(dateStr, shift, selected)
                        }
                        disabled={isConfirmed}
                      />

                      {!isConfirmed && (
                        <button
                          onClick={() => handleConfirm(dateStr, shift)}
                          className="mt-2 self-start rounded-md bg-twohas px-3 py-1 text-xs font-semibold text-white hover:bg-opacity-90 transition"
                        >
                          Confirm
                        </button>
                      )}
                    </div>
                  );
                })}
            </div>
          );
        })}
      </div>
    </div>
  );
}
