import React, { useState } from "react";
import ScheduleCell from "./ScheduleCell";

export default function Schedule() {
  const today = new Date();
  const year = today.getFullYear();
  const month = today.getMonth();

  const firstDay = new Date(year, month, 1).getDay();
  const lastDate = new Date(year, month + 1, 0).getDate();

  const totalCells = 42;
  const calendarDays = [];

  for (let i = 0; i < firstDay; i++) {
    calendarDays.push({ day: null, isCurrentMonth: false });
  }

  for (let i = 1; i <= lastDate; i++) {
    calendarDays.push({ day: i, isCurrentMonth: true });
  }

  const remaining = totalCells - calendarDays.length;
  for (let i = 1; i <= remaining; i++) {
    calendarDays.push({ day: i, isCurrentMonth: false });
  }

  const [schedule, setSchedule] = useState({});

  const handleChange = (dateStr, shift, selectedEmployees) => {
    setSchedule((prev) => {
      const dayData = prev[dateStr] || {
        AM: { employees: [], confirmed: false },
        PM: { employees: [], confirmed: false },
      };
      return {
        ...prev,
        [dateStr]: {
          ...dayData,
          [shift]: {
            ...dayData[shift],
            employees: selectedEmployees,
          },
        },
      };
    });
  };

  const handleConfirm = (dateStr, shift) => {
    setSchedule((prev) => {
      const dayData = prev[dateStr] || {};
      const shiftData = dayData[shift] || { employees: [] };

      if (shiftData.employees.length === 0) return prev;

      return {
        ...prev,
        [dateStr]: {
          ...dayData,
          [shift]: {
            ...shiftData,
            confirmed: true,
          },
        },
      };
    });
  };

  return (
    <div className="p-6 max-w-7xl mx-auto">
      <h2 className="text-3xl font-bold mb-6 text-center">
        {year}-{month + 1}
      </h2>

      <div className="grid grid-cols-7 gap-4 text-center text-sm font-semibold text-gray-600 mb-4">
        {["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"].map((d) => (
          <div key={d} className="py-3 bg-gray-100 rounded-md shadow-sm">
            {d}
          </div>
        ))}
      </div>

      <div className="grid grid-cols-7 gap-4 text-sm">
        {calendarDays.map(({ day, isCurrentMonth }, index) => {
          if (!day) {
            return (
              <div
                key={index}
                className="h-64 min-w-[120px] bg-transparent border-none"
              />
            );
          }

          const cellDate = new Date(
            isCurrentMonth ? year : month === 11 ? year + 1 : year,
            isCurrentMonth ? month : (month + 1) % 12,
            day
          );

          const dateStr = `${cellDate.getFullYear()}-${String(
            cellDate.getMonth() + 1
          ).padStart(2, "0")}-${String(day).padStart(2, "0")}`;

          const dayData = schedule[dateStr] || {};

          return (
            <ScheduleCell
              key={index}
              dateStr={dateStr}
              day={day}
              dayData={dayData}
              handleChange={handleChange}
              handleConfirm={handleConfirm}
            />
          );
        })}
      </div>
    </div>
  );
}
