import React, { useState } from "react";
import ScheduleCell from "./Scedule Components/ScheduleCell";

export default function Schedule() {
  const today = new Date();
  const todayDate = today.getDate();
  const todayDay = today.getDay();
  const diffToMonday = todayDay - 1;
  const startDate = todayDate + 7 - diffToMonday;
  const printedDays = [];
  for (let i = startDate; i < startDate + 14; i++) {
    printedDays.push({ i });
  }

  const [schedule, setSchedule] = useState({});

  const handleChange = (dateStr, shift, selectedEmployees) => {
    setSchedule((prev) => {
      const dayData = prev[dateStr] || {
        AM: { employees: [], confirmed: false },
        PM: { employees: [], confirmed: false },
      };
      return {
        ...prev,
        [dateStr]: {
          ...dayData,
          [shift]: {
            ...dayData[shift],
            employees: selectedEmployees,
          },
        },
      };
    });
  };

  const handleConfirm = (dateStr, shift) => {
    setSchedule((prev) => {
      const dayData = prev[dateStr] || {};
      const shiftData = dayData[shift] || { employees: [] };

      if (shiftData.employees.length === 0) return prev;

      return {
        ...prev,
        [dateStr]: {
          ...dayData,
          [shift]: {
            ...shiftData,
            confirmed: true,
          },
        },
      };
    });
  };

  return (
    <div className="p-6 max-w-7xl mx-auto">
      <h2 className="text-3xl font-bold mb-6 text-center">Make a Schedule</h2>
      <div className="grid grid-cols-7 gap-4 text-center text-sm font-bold text-gray-1000 mb-4">
        {["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"].map((d) => (
          <div key={d} className="py-3 bg-gray-100 rounded-md shadow-sm">
            {d}
          </div>
        ))}
      </div>

      <div className="grid grid-cols-7 gap-4 text-sm">
        {printedDays.map(({ day, isCurrentMonth, dateObj }, index) => {
          if (!day) {
            return (
              <div
                key={index}
                className="h-64 min-w-[120px] bg-transparent border-none"
              />
            );
          }

          const dateStr = `${dateObj.getFullYear()}-${String(
            dateObj.getMonth() + 1
          ).padStart(2, "0")}-${String(day).padStart(2, "0")}`;

          const dayData = schedule[dateStr] || {};

          return (
            <ScheduleCell
              key={index}
              dateStr={dateStr}
              day={day}
              dayData={dayData}
              handleChange={handleChange}
              handleConfirm={handleConfirm}
            />
          );
        })}
      </div>
    </div>
  );
}
