import React from "react";

export default function RecipeTable({
  recipes,
  setEditingId,
  editedRecipe,
  setEditedRecipe,
  setRecipes,
  editingId,
}) {
  const handleEdit = (recipe) => {
    setEditingId(recipe.id);
    setEditedRecipe(recipe);
  };

  const handleDelete = (id) => {
    setRecipes((prev) => prev.filter((r) => r.id !== id));
  };

  const handleIngredientChange = (index, value) => {
    const newIngredients = [...editedRecipe.ingredients];
    newIngredients[index] = value;
    setEditedRecipe({ ...editedRecipe, ingredients: newIngredients });
  };

  const addIngredientField = () => {
    setEditedRecipe({
      ...editedRecipe,
      ingredients: [...editedRecipe.ingredients, ""],
    });
  };

  const handleSave = () => {
    setRecipes(recipes.map((r) => (r.id === editingId ? editedRecipe : r)));
    setEditingId(null);
  };

  const handleCancel = () => {
    setEditingId(null);
  };

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
      {recipes.map((recipe) => (
        <div
          key={recipe.id}
          className="bg-white p-4 rounded-xl shadow flex flex-col justify-between"
        >
          {editingId === recipe.id ? (
            <>
              <input
                type="text"
                value={editedRecipe.name}
                onChange={(e) =>
                  setEditedRecipe({ ...editedRecipe, name: e.target.value })
                }
                className="border px-2 py-1 rounded mb-2 w-full"
              />
              <input
                type="text"
                value={editedRecipe.volume}
                onChange={(e) =>
                  setEditedRecipe({ ...editedRecipe, volume: e.target.value })
                }
                className="border px-2 py-1 rounded mb-2 w-full"
              />
              <div className="grid grid-cols-5 gap-2 mb-2">
                {editedRecipe.ingredients.map((ing, idx) => (
                  <input
                    key={idx}
                    value={ing}
                    onChange={(e) =>
                      handleIngredientChange(idx, e.target.value)
                    }
                    className="border px-2 py-1 rounded w-full"
                  />
                ))}
              </div>
              <button
                type="button"
                onClick={addIngredientField}
                className="py-1 text-blue-500 text-sm font-medium mt-2 hover:underline"
              >
                + Add Ingredient
              </button>
              <div className="flex gap-2 mt-2">
                <button
                  onClick={handleSave}
                  className="bg-green-500 hover:bg-green-600 text-white px-3 py-1 rounded-md text-sm"
                >
                  Save
                </button>
                <button
                  onClick={handleCancel}
                  className="bg-gray-400 hover:bg-gray-500 text-white px-3 py-1 rounded-md text-sm"
                >
                  Cancel
                </button>
              </div>
            </>
          ) : (
            <>
              <h3 className="text-lg font-bold mb-2">{recipe.name}</h3>
              <p className="mb-2">Volume: {recipe.volume}</p>
              <div className="grid grid-cols-5 gap-2 mb-2">
                {recipe.ingredients.map((ing, idx) => (
                  <span
                    key={idx}
                    className="bg-gray-200 rounded px-2 py-1 text-sm"
                  >
                    {ing.name} ({ing.volume})
                  </span>
                ))}
              </div>

              <div className="flex gap-2 mt-auto">
                <button
                  onClick={() => handleEdit(recipe)}
                  className="bg-yellow-500 hover:bg-yellow-600 text-white px-3 py-1 rounded-md text-sm"
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDelete(recipe.id)}
                  className="bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded-md text-sm"
                >
                  Delete
                </button>
              </div>
            </>
          )}
        </div>
      ))}
    </div>
  );
}
