import React from "react";
import { NavLink, Outlet } from "react-router-dom";
import { useUser } from "../context/usercontext";

export default function AdminPage() {
  const { logout } = useUser();

  return (
    <div className="flex flex-col min-h-screen bg-white text-black">
      <header className="bg-twohas text-white px-6 py-4 shadow-md flex justify-between items-center">
        <div className="flex items-center gap-4">
          <h1 className="text-2xl font-bold select-none">Admin Panel</h1>
          <button
            onClick={logout}
            className="text-sm bg-white px-3 py-1 rounded-lg hover:bg-red-600 transition"
          >
            Log out
          </button>
        </div>
        <nav className="flex gap-6 text-lg font-semibold">
          <NavLink
            to="Employee"
            className={({ isActive }) =>
              `transition ${
                isActive ? "border-b-2 border-white" : "hover:text-gray-300"
              }`
            }
          >
            Employee
          </NavLink>
          <NavLink
            to="Schedule"
            className={({ isActive }) =>
              `transition ${
                isActive ? "border-b-2 border-white" : "hover:text-gray-300"
              }`
            }
          >
            Schedule
          </NavLink>
          <NavLink
            to="Tip"
            className={({ isActive }) =>
              `transition ${
                isActive ? "border-b-2 border-white" : "hover:text-gray-300"
              }`
            }
          >
            Tip
          </NavLink>
          <NavLink
            to="CPA"
            className={({ isActive }) =>
              `transition ${
                isActive ? "border-b-2 border-white" : "hover:text-gray-300"
              }`
            }
          >
            CPA
          </NavLink>
          <NavLink
            to="Recipe"
            className={({ isActive }) =>
              `transition ${
                isActive ? "border-b-2 border-white" : "hover:text-gray-300"
              }`
            }
          >
            Recipe
          </NavLink>
          <NavLink
            to="Inventory"
            className={({ isActive }) =>
              `transition ${
                isActive ? "border-b-2 border-white" : "hover:text-gray-300"
              }`
            }
          >
            Inventory
          </NavLink>
        </nav>
      </header>
      <main className="flex-1 p-8 overflow-auto z-15">
        <Outlet />
      </main>
    </div>
  );
}
