import React, { useState } from "react";
import API from "../../../API/api";

export default function UserInventoryTable({ inventoryList, fetchInventory }) {
  const [editingId, setEditingId] = useState(null);
  const [editedInventory, setEditedInventory] = useState(null);

  const handleEdit = (item) => {
    setEditingId(item._id);
    setEditedInventory(item);
  };

  const handleChange = (value) => {
    setEditedInventory({ ...editedInventory, inStock: value });
  };

  const handleSave = async () => {
    try {
      const token = localStorage.getItem("token");
      const payload = { ...editedInventory, id: editedInventory._id };
      await API.put("inventory/updateInventory", payload, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setEditingId(null);
      await fetchInventory();
    } catch (error) {
      console.error("Failed to Upload", error);
    }
  };

  return (
    <div className="overflow-x-auto rounded-xl shadow-lg bg-white p-4">
      <table className="min-w-full table-auto border border-gray-200">
        <thead className="bg-gray-100">
          <tr>
            <th className="px-3 py-2 border-b text-left text-sm font-semibold text-gray-600 w-16">
              Item#
            </th>
            <th className="px-3 py-2 border-b text-left text-sm font-semibold text-gray-600">
              Name
            </th>
            <th className="px-3 py-2 border-b text-left text-sm font-semibold text-gray-600 w-24">
              In Stock
            </th>
            <th className="px-3 py-2 border-b text-center text-sm font-semibold text-gray-600 w-24">
              Action
            </th>
          </tr>
        </thead>
        <tbody className="divide-y divide-gray-200">
          {Array.isArray(inventoryList) && inventoryList.length === 0 ? (
            <tr>
              <td colSpan={4} className="text-center py-6 text-gray-400 italic">
                No items added yet.
              </td>
            </tr>
          ) : (
            Array.isArray(inventoryList) &&
            inventoryList.map((item, index) => (
              <tr key={item._id} className="hover:bg-gray-50">
                <td className="px-3 py-2 text-sm">{index + 1}</td>
                <td className="px-3 py-2 text-sm">{item.name}</td>
                <td className="px-3 py-2 text-sm">
                  {editingId === item._id ? (
                    <input
                      type="number"
                      value={editedInventory.inStock}
                      onChange={(e) => handleChange(e.target.value)}
                      className="border rounded px-2 py-1 w-20"
                    />
                  ) : (
                    item.inStock
                  )}
                </td>
                <td className="px-3 py-2 text-center">
                  {editingId === item._id ? (
                    <button
                      onClick={handleSave}
                      className="bg-green-500 hover:bg-green-600 text-white text-xs font-semibold px-3 py-1 rounded-full"
                    >
                      Save
                    </button>
                  ) : (
                    <button
                      onClick={() => handleEdit(item)}
                      className="bg-blue-500 hover:bg-blue-600 text-white text-xs font-semibold px-3 py-1 rounded-full"
                    >
                      Edit
                    </button>
                  )}
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
}
