import React, { useState } from "react";

const EMPLOYEES = ["홍길동", "김민수", "최유진", "박서준"];

export default function Schedule() {
  const today = new Date();
  const year = today.getFullYear();
  const month = today.getMonth();

  const firstDay = new Date(year, month, 1).getDay();
  const lastDate = new Date(year, month + 1, 0).getDate();

  const calendarDays = [];
  for (let i = 0; i < firstDay; i++) calendarDays.push(null);
  for (let i = 1; i <= lastDate; i++) calendarDays.push(i);

  const [schedule, setSchedule] = useState({});

  const handleSelectChange = (e, dateStr, shift) => {
    const selected = Array.from(e.target.selectedOptions).map((o) => o.value);
    setSchedule((prev) => {
      const dayData = prev[dateStr] || {
        AM: { employees: [], confirmed: false },
        PM: { employees: [], confirmed: false },
      };
      return {
        ...prev,
        [dateStr]: {
          ...dayData,
          [shift]: {
            ...dayData[shift],
            employees: selected,
          },
        },
      };
    });
  };

  const handleConfirm = (dateStr, shift) => {
    setSchedule((prev) => {
      const dayData = prev[dateStr] || {};
      return {
        ...prev,
        [dateStr]: {
          ...dayData,
          [shift]: {
            ...(dayData[shift] || { employees: [] }),
            confirmed: true,
          },
        },
      };
    });
  };

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">
        {year}년 {month + 1}월 스케줄
      </h2>

      <div className="grid grid-cols-7 gap-2 text-center text-sm font-semibold text-gray-600 mb-2">
        {["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"].map((d) => (
          <div key={d} className="py-2 bg-gray-100 rounded">
            {d}
          </div>
        ))}
      </div>

      <div className="grid grid-cols-7 gap-2 text-sm">
        {calendarDays.map((day, index) => {
          const dateStr = day
            ? `${year}-${String(month + 1).padStart(2, "0")}-${String(
                day
              ).padStart(2, "0")}`
            : null;
          const dayData = schedule[dateStr] || {};

          return (
            <div
              key={index}
              className="h-[200px] border rounded-lg bg-white shadow-sm overflow-hidden flex flex-col justify-between"
            >
              <div className="px-2 pt-1 text-right text-xs text-gray-500 h-5">
                {day}
              </div>

              {["AM", "PM"].map((shift) => {
                const shiftData = dayData[shift] || {
                  employees: [],
                  confirmed: false,
                };
                const isConfirmed = shiftData.confirmed;

                return (
                  <div
                    key={shift}
                    className={`flex-1 border-t px-2 py-1 transition flex flex-col gap-1 ${
                      isConfirmed ? "bg-twohas text-white" : "hover:bg-blue-50"
                    }`}
                  >
                    <div className="text-xs font-semibold">
                      {shift === "AM" ? "오전" : "오후"}
                    </div>

                    <select
                      multiple
                      disabled={isConfirmed}
                      value={shiftData.employees}
                      onChange={(e) => handleSelectChange(e, dateStr, shift)}
                      className={`w-full text-xs bg-white border rounded p-1 h-full ${
                        isConfirmed
                          ? "bg-twohas text-white"
                          : "bg-gray-50 text-black"
                      }`}
                    >
                      {EMPLOYEES.map((name) => (
                        <option key={name} value={name}>
                          {name}
                        </option>
                      ))}
                    </select>

                    {!isConfirmed && (
                      <button
                        onClick={() => handleConfirm(dateStr, shift)}
                        className="mt-1 text-[10px] bg-twohas text-white rounded px-2 py-0.5 hover:brightness-110"
                      >
                        Confirm
                      </button>
                    )}
                  </div>
                );
              })}
            </div>
          );
        })}
      </div>
    </div>
  );
}
