import React from "react";

export default function CPAEmployeeGrid({ employees, scheduleData, dates }) {
  return (
    <div className="overflow-x-auto w-full">
      <div className="grid grid-cols-[200px_100px_120px_repeat(7,120px)] auto-rows-[40px] border border-gray-300">
        <div className="border p-2 font-bold bg-gray-100 sticky top-0 z-10">
          Name
        </div>
        <div className="border p-2 font-bold bg-gray-100 sticky top-0 z-10">
          Hourly Pay
        </div>
        <div className="border p-2 font-bold bg-gray-100 sticky top-0 z-10">
          Tax Report
        </div>
        {dates.map((date) => (
          <div
            key={`header-${date}`}
            className="border p-2 text-center font-bold bg-gray-100 sticky top-0 z-10 col-span-1"
          >
            {date}
          </div>
        ))}

        {employees.map((emp) => (
          <React.Fragment key={emp._id}>
            <div className="border p-2 font-semibold sticky left-0 bg-white z-10">
              {emp.name}
            </div>
            <div className="border p-2 text-center">{emp.hourlyPay}</div>
            <div className="border p-2 text-center">
              {" "}
              {/* Tax report placeholder */}{" "}
            </div>

            {dates.map((date) => {
              const schedule = scheduleData[date] || {
                AM: { hourly: 0, tip: 0 },
                PM: { hourly: 0, tip: 0 },
              };
              return (
                <div
                  key={`${emp._id}-${date}`}
                  className="border p-1 grid grid-rows-4 text-xs text-center"
                >
                  <div className="row-span-1 font-semibold">{date}</div>
                  <div className="row-span-1">AM</div>
                  <div className="row-span-1">{schedule.AM.hourly}</div>
                  <div className="row-span-1">{schedule.AM.tip}</div>
                  <div className="row-span-1">PM</div>
                  <div className="row-span-1">{schedule.PM.hourly}</div>
                  <div className="row-span-1">{schedule.PM.tip}</div>
                </div>
              );
            })}
          </React.Fragment>
        ))}
      </div>
    </div>
  );
}
